import React, { useEffect, useState } from 'react';
import api from './api'; // Ensure this points to your axios instance

const FriendsList = ({ onSelectUser, refreshTrigger }) => {
    const [friends, setFriends] = useState([]);
    const [loading, setLoading] = useState(false);

    // 1. Fetch the list of accepted friends from the backend
    const fetchFriends = async () => {
        setLoading(true);
        try {
            const res = await api.get('friends/');
            setFriends(res.data);
        } catch (err) {
            console.error("Error fetching friends list:", err);
        } finally {
            setLoading(false);
        }
    };

    // 2. Re-fetch friends whenever the component mounts or the refreshTrigger changes
    useEffect(() => {
        fetchFriends();
    }, [refreshTrigger]);

    // 3. Handle the Unfriend/Remove action
    const handleUnfriend = async (inviteId) => {
        const confirmRemove = window.confirm("Are you sure you want to remove this friend? You won't be able to chat until you invite them again.");
        
        if (confirmRemove) {
            try {
                await api.delete(`friends/remove/${inviteId}/`);
                // Refresh the list locally after successful deletion
                setFriends(prev => prev.filter(f => f.invite_id !== inviteId));
                // Clear the active chat if it was with this user
                onSelectUser(null);
                alert("Friend removed successfully.");
            } catch (err) {
                console.error("Failed to remove friend:", err);
                alert("Could not remove friend. Please try again.");
            }
        }
    };

    return (
        <div style={styles.container}>
            <h4 style={styles.title}>Your Friends</h4>
            
            {loading && <p style={styles.infoText}>Updating list...</p>}
            
            {!loading && friends.length === 0 && (
                <p style={styles.infoText}>You haven't added any friends yet. Use the search bar above to find people!</p>
            )}

            <div style={styles.list}>
                {friends.map(friend => (
                    <div key={friend.id} style={styles.friendItem}>
                        <div 
                            style={styles.userInfo} 
                            onClick={() => onSelectUser({ id: friend.id, username: friend.username })}
                            title={`Chat with ${friend.username}`}
                        >
                            <span style={styles.avatar}>ðŸ‘¤</span>
                            <span style={styles.username}>{friend.username}</span>
                        </div>
                        
                        <button 
                            onClick={() => handleUnfriend(friend.invite_id)}
                            style={styles.removeBtn}
                            title="Remove Friend"
                        >
                            Remove
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

// Component-specific styles
const styles = {
    container: {
        marginTop: '10px',
    },
    title: {
        fontSize: '16px',
        color: '#495057',
        marginBottom: '15px',
        borderBottom: '1px solid #eee',
        paddingBottom: '5px'
    },
    infoText: {
        fontSize: '13px',
        color: '#868e96',
        fontStyle: 'italic'
    },
    list: {
        display: 'flex',
        flexDirection: 'column',
        gap: '8px'
    },
    friendItem: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '10px',
        backgroundColor: '#fff',
        borderRadius: '6px',
        border: '1px solid #f1f3f5',
        transition: 'background-color 0.2s'
    },
    userInfo: {
        display: 'flex',
        alignItems: 'center',
        cursor: 'pointer',
        flex: 1
    },
    avatar: {
        marginRight: '10px',
        fontSize: '18px'
    },
    username: {
        fontWeight: '600',
        color: '#343a40',
        fontSize: '14px'
    },
    removeBtn: {
        background: 'none',
        border: 'none',
        color: '#fa5252',
        fontSize: '11px',
        cursor: 'pointer',
        fontWeight: 'bold',
        padding: '5px'
    }
};

export default FriendsList;
