import React, { useState, useEffect, useCallback } from 'react';
import Login from './Login';
import Register from './Register';
import UserSearch from './UserSearch';
import Chat from './Chat';
import FriendsList from './FriendsList';
import api from './api'; // <--- IMPORTANT: This uses your interceptor logic

function App() {
  const [authUser, setAuthUser] = useState(localStorage.getItem('username'));
  const [activeChat, setActiveChat] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [invites, setInvites] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState({}); 
  const [refreshFriends, setRefreshFriends] = useState(0);

  // Status Socket Logic (Online/Offline)
  useEffect(() => {
    if (!authUser) return;

    // Use the username-based URL to bypass AnonymousUser issues
    const statusSocket = new WebSocket(`ws://localhost:8000/ws/status/${authUser}/`);

    statusSocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setOnlineUsers(prev => ({ ...prev, [data.user]: data.status }));

      if (data.status === 'online' && data.user !== authUser) {
        if (Notification.permission === "granted") {
          new Notification("Social App", { body: `${data.user} is now active!` });
        }
      }
    };
    return () => statusSocket.close();
  }, [authUser]);

  // Fetching Invites using the 'api' instance
  const fetchInvites = useCallback(async () => {
    if (authUser) {
      try {
        const res = await api.get('invitations/'); // This now sends the Token!
        setInvites(res.data);
      } catch (err) {
        console.error("401 Error usually means the token is missing/expired:", err);
      }
    }
  }, [authUser]);

  useEffect(() => {
    fetchInvites();
    const interval = setInterval(fetchInvites, 10000);
    return () => clearInterval(interval);
  }, [fetchInvites]);

  const handleInviteAction = async (inviteId, action) => {
    try {
      await api.post('invitations/respond/', { invite_id: inviteId, action });
      setInvites(prev => prev.filter(i => i.id !== inviteId));
      if (action === 'accepted') setRefreshFriends(prev => prev + 1);
    } catch (err) { console.error("Action failed", err); }
  };

  const handleLogout = () => {
    localStorage.clear();
    setAuthUser(null);
    window.location.reload();
  };

  if (!authUser) {
    return (
      <div style={{ maxWidth: '400px', margin: '100px auto' }}>
        {showRegister ? <Register /> : <Login setAuthUser={setAuthUser} />}
        <button onClick={() => setShowRegister(!showRegister)} style={{ width: '100%', marginTop: '10px', background: 'none', border: 'none', color: 'blue', cursor: 'pointer' }}>
          {showRegister ? "Go to Login" : "Create Account"}
        </button>
      </div>
    );
  }

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <div style={{ width: '350px', borderRight: '1px solid #ddd', padding: '20px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h3>Chats</h3>
          <button onClick={handleLogout} style={{ height: '30px' }}>Logout</button>
        </div>

        <UserSearch onSelectUser={setActiveChat} />

        {invites.length > 0 && (
          <div style={{ background: '#fff3cd', padding: '10px', marginTop: '10px' }}>
            <p style={{ margin: 0, fontWeight: 'bold' }}>New Requests</p>
            {invites.map(i => (
              <div key={i.id} style={{ display: 'flex', justifyContent: 'space-between', marginTop: '5px' }}>
                <span>{i.sender}</span>
                <button onClick={() => handleInviteAction(i.id, 'accepted')}>Accept</button>
              </div>
            ))}
          </div>
        )}

        <FriendsList 
          onSelectUser={setActiveChat} 
          refreshTrigger={refreshFriends} 
          onlineUsers={onlineUsers} 
        />
      </div>

      <div style={{ flex: 1 }}>
        {activeChat ? (
          <Chat receiverId={activeChat.id} receiverName={activeChat.username} />
        ) : (
          <div style={{ textAlign: 'center', marginTop: '200px', color: '#888' }}>
            <h2>Welcome, {authUser}!</h2>
            <p>Select a friend to start chatting.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
