import React, { useState, useEffect } from 'react';
import api from './api'; // Important: Use your axios instance with the JWT token

const FriendsList = ({ onSelectFriend, selectedFriendId }) => {
    const [friends, setFriends] = useState([]);
    const [loading, setLoading] = useState(true);

    // 1. Fetch the friends list from the backend
    useEffect(() => {
        const fetchFriends = async () => {
            try {
                // Hits the /api/friends/ endpoint we set up in Django
                const res = await api.get('friends/');
                setFriends(res.data);
                setLoading(false);
            } catch (err) {
                console.error("❌ Could not load friends:", err);
                setLoading(false);
            }
        };

        fetchFriends();
    }, []);

    if (loading) return <div style={styles.loading}>Loading friends...</div>;

    return (
        <div style={styles.container}>
            <h3 style={styles.title}>Recent Chats</h3>
            
            <div style={styles.list}>
                {friends.length === 0 ? (
                    <div style={styles.empty}>No friends found. Use search to find someone!</div>
                ) : (
                    friends.map((friend) => (
                        <div 
                            key={friend.id} 
                            onClick={() => onSelectFriend(friend)}
                            style={{
                                ...styles.friendItem,
                                backgroundColor: selectedFriendId === friend.id ? '#1e293b' : 'transparent',
                                borderLeft: selectedFriendId === friend.id ? '4px solid #6366f1' : '4px solid transparent'
                            }}
                        >
                            <div style={styles.avatar}>
                                {friend.username[0].toUpperCase()}
                            </div>
                            <div style={styles.info}>
                                <div style={styles.name}>{friend.username}</div>
                                <div style={styles.status}>Online</div>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

const styles = {
    container: { display: 'flex', flexDirection: 'column', height: '100%', background: '#0f172a' },
    title: { color: '#94a3b8', fontSize: '12px', textTransform: 'uppercase', padding: '20px', letterSpacing: '1px' },
    list: { flex: 1, overflowY: 'auto' },
    friendItem: { 
        display: 'flex', 
        alignItems: 'center', 
        padding: '12px 20px', 
        cursor: 'pointer', 
        transition: 'all 0.2s ease',
        gap: '12px'
    },
    avatar: { 
        width: '40px', 
        height: '40px', 
        borderRadius: '12px', 
        background: '#6366f1', 
        color: '#fff', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center', 
        fontWeight: 'bold' 
    },
    info: { display: 'flex', flexDirection: 'column' },
    name: { color: '#f8fafc', fontWeight: '600', fontSize: '14px' },
    status: { color: '#10b981', fontSize: '11px' },
    loading: { color: '#64748b', padding: '20px', textAlign: 'center' },
    empty: { color: '#475569', padding: '20px', textAlign: 'center', fontSize: '13px' }
};

export default FriendsList;
