import React, { useState, useEffect } from 'react';
import Login from './Login';
import Register from './Register';
import UserSearch from './UserSearch';
import Chat from './Chat';
import FriendsList from './FriendsList';
import api from './api';

function App() {
  const [authUser, setAuthUser] = useState(localStorage.getItem('username'));
  const [activeChat, setActiveChat] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [invites, setInvites] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState({}); // Stores { username: "online/offline" }
  const [refreshFriends, setRefreshFriends] = useState(0);

  // Request Browser Notification Permission
  useEffect(() => {
    if ("Notification" in window && Notification.permission === "default") {
      Notification.requestPermission();
    }
  }, []);

  // 1. Status & Heartbeat Socket (Global)
  useEffect(() => {
    if (authUser) {
      const statusSocket = new WebSocket(`ws://localhost:8000/ws/status/`);

      statusSocket.onmessage = (event) => {
        const data = JSON.parse(event.data);

        // Update local status state
        setOnlineUsers(prev => ({
          ...prev,
          [data.user]: data.status
        }));

        // Notification if a friend comes online
        if (data.status === 'online' && data.user !== authUser && Notification.permission === "granted") {
          new Notification("Social Chat", { body: `${data.user} is now online!` });
        }
      };

      statusSocket.onclose = () => console.log("Status Socket Closed");
      return () => statusSocket.close();
    }
  }, [authUser]);

  // 2. Fetch Pending Invitations
  const fetchInvites = async () => {
    if (authUser) {
      try {
        const res = await api.get('invitations/');
        setInvites(res.data);
      } catch (err) { console.error("Error fetching invites"); }
    }
  };

  useEffect(() => {
    fetchInvites();
    const interval = setInterval(fetchInvites, 10000);
    return () => clearInterval(interval);
  }, [authUser]);

  const handleInviteAction = async (inviteId, action) => {
    try {
      await api.post('invitations/respond/', { invite_id: inviteId, action });
      setInvites(prev => prev.filter(i => i.id !== inviteId));
      if (action === 'accepted') setRefreshFriends(prev => prev + 1);
    } catch (err) { alert("Action failed"); }
  };

  if (!authUser) {
    return (
      <div style={styles.authBox}>
        {showRegister ? <Register /> : <Login setAuthUser={setAuthUser} />}
        <button onClick={() => setShowRegister(!showRegister)} style={styles.linkBtn}>
          {showRegister ? "Back to Login" : "Create Account"}
        </button>
      </div>
    );
  }

  return (
    <div style={styles.layout}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <div style={styles.header}>
          <h3>Platform</h3>
          <span>Hello, <b>{authUser}</b></span>
          <button onClick={() => { localStorage.clear(); window.location.reload(); }} style={styles.logoutBtn}>Logout</button>
        </div>

        <UserSearch onSelectUser={setActiveChat} />

        {/* Invitations Inbox */}
        {invites.length > 0 && (
          <div style={styles.inbox}>
            <h6>Pending Requests</h6>
            {invites.map(i => (
              <div key={i.id} style={styles.inboxItem}>
                <span>{i.sender}</span>
                <button onClick={() => handleInviteAction(i.id, 'accepted')} style={styles.btnSmallG}>Accept</button>
              </div>
            ))}
          </div>
        )}

        <hr />

        {/* Friends List with Online Props */}
        <FriendsList 
          onSelectUser={setActiveChat} 
          refreshTrigger={refreshFriends} 
          onlineUsers={onlineUsers} 
        />
      </div>

      {/* Main Area */}
      <div style={styles.main}>
        {activeChat ? (
          <Chat receiverId={activeChat.id} receiverName={activeChat.username} />
        ) : (
          <div style={styles.emptyState}>Select a friend to start messaging</div>
        )}
      </div>
    </div>
  );
}

const styles = {
  layout: { display: 'flex', height: '100vh', backgroundColor: '#f0f2f5' },
  sidebar: { width: '350px', backgroundColor: '#fff', borderRight: '1px solid #ddd', padding: '20px', overflowY: 'auto' },
  main: { flex: 1, padding: '20px' },
  authBox: { maxWidth: '400px', margin: '100px auto', padding: '20px', background: '#fff', borderRadius: '8px' },
  header: { marginBottom: '20px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' },
  inbox: { background: '#fff3cd', padding: '10px', borderRadius: '5px', marginTop: '10px' },
  inboxItem: { display: 'flex', justifyContent: 'space-between', marginTop: '5px' },
  btnSmallG: { background: 'green', color: 'white', border: 'none', fontSize: '10px', padding: '2px 5px', borderRadius: '3px' },
  emptyState: { textAlign: 'center', marginTop: '200px', color: '#999', fontSize: '20px' },
  logoutBtn: { background: '#ff4d4d', color: '#fff', border: 'none', padding: '5px', borderRadius: '4px', cursor: 'pointer' },
  linkBtn: { width: '100%', marginTop: '10px', background: 'none', border: 'none', color: 'blue', cursor: 'pointer' }
};

export default App;
