import React, { useState, useEffect } from 'react';
import api from './api'; 
import UserSearch from './UserSearch';
import FriendsList from './FriendsList';
import Chat from './Chat';

function App() {
    // 1. DEFINE THE STATE
    const [friends, setFriends] = useState([]);
    const [selectedFriend, setSelectedFriend] = useState(null);
    const authUser = localStorage.getItem('username');

    // 2. DEFINE THE HANDLER FOR SEARCH SELECTION
    const handleUserSelected = (user) => {
        // Prevent adding duplicates to the UI list
        const exists = friends.find(f => f.username === user.username);
        if (!exists) {
            setFriends(prev => [user, ...prev]);
        }
        // Open the chat window for this user
        setSelectedFriend(user);
    };

    return (
        <div style={styles.appContainer}>
            {/* SIDEBAR: Search + Friends List */}
            <div style={styles.sidebar}>
                <div style={styles.header}>
                    <h2 style={{ color: '#fff', margin: 0 }}>Messages</h2>
                </div>

                {/* USER SEARCH COMPONENT */}
                <UserSearch onSelectUser={handleUserSelected} />

                {/* FRIENDS LIST COMPONENT */}
                <FriendsList 
                    onSelectFriend={(friend) => setSelectedFriend(friend)} 
                    selectedFriendId={selectedFriend?.id} 
                />
            </div>

            {/* CHAT AREA */}
            <div style={styles.chatArea}>
                {selectedFriend ? (
                    <Chat 
                        receiverId={selectedFriend.id} 
                        receiverName={selectedFriend.username} 
                    />
                ) : (
                    <div style={styles.welcomeScreen}>
                        <div style={styles.welcomeIcon}>ðŸ’¬</div>
                        <h3 style={{ color: '#fff' }}>Welcome, {authUser}!</h3>
                        <p style={{ color: '#64748b' }}>Select a friend to start chatting.</p>
                    </div>
                )}
            </div>
        </div>
    );
}

// --- STYLES ---
const styles = {
    appContainer: {
        display: 'flex',
        height: '100vh',
        width: '100vw',
        background: '#020617',
        overflow: 'hidden',
    },
    sidebar: {
        width: '320px',
        display: 'flex',
        flexDirection: 'column',
        borderRight: '1px solid rgba(255,255,255,0.05)',
        background: '#0f172a',
    },
    header: {
        padding: '24px',
        borderBottom: '1px solid rgba(255,255,255,0.05)',
    },
    chatArea: {
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
    },
    welcomeScreen: {
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
    },
    welcomeIcon: {
        fontSize: '50px',
        marginBottom: '20px',
        background: 'rgba(99, 102, 241, 0.1)',
        width: '100px',
        height: '100px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: '50%',
    }
};

export default App;
