    fontWeight: 'bold', 
    color: '#fff', 
    marginBottom: '40px' 
  },
  navLinks: { 
    flex: 1, 
    display: 'flex', 
    flexDirection: 'column', 
    gap: '30px' 
  },
  activeIcon: { 
    fontSize: '24px', 
    background: '#1e293b', 
