  },
  logoutBtn: { 
    border: 'none', 
    background: 'none', 
    color: '#ef4444', 
    fontWeight: '800', 
    cursor: 'pointer', 
    fontSize: '10px', 
    marginTop: 'auto' 
  },
  sidebar: { 
    width: '360px', 
    background: '#020617', 
    borderRight: '1px solid #1e293b', 
    display: 'flex', 
    flexDirection: 'column' 
  },
  sideHeader: { 
    padding: '30px 25px' 
  },
  userProfile: { 
    display: 'flex', 
    alignItems: 'center', 
    gap: '12px', 
    marginBottom: '25px', 
    padding: '12px', 
    background: 'rgba(255,255,255,0.03)', 
    borderRadius: '15px' 
  },
  avatarSmall: { 
    width: '36px', 
    height: '36px', 
    borderRadius: '10px', 
    background: 'linear-gradient(135deg, #6366f1, #a855f7)', 
    display: 'flex', 
