import React, { useState } from 'react';
import api from './api'; // Use the custom api instance

const Login = ({ setAuthUser }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    // Inside your Login function in Login.js
// Inside your Login function in Login.js
const handleLogin = async (e) => {
    e.preventDefault();
    try {
        const res = await axios.post('http://localhost:8000/api/login/', { username, password });
        
        // 1. Save the access token for the interceptor
        localStorage.setItem('access_token', res.data.access); 
        
        // 2. Save the username for the UI
        localStorage.setItem('username', username);
        
        setAuthUser(username);
        
        // 3. IMPORTANT: Refresh to make sure the API interceptor is active with the new token
        window.location.reload(); 
    } catch (err) {
        alert("Login failed! Check your credentials.");
    }
};

    return (
        <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '300px', margin: '20px auto', padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
            <h3 style={{ textAlign: 'center' }}>Login</h3>
            <input 
                type="text" 
                placeholder="Username" 
                value={username}
                onChange={(e) => setUsername(e.target.value)} 
                required 
                style={{ padding: '8px' }}
            />
            <input 
                type="password" 
                placeholder="Password" 
                value={password}
                onChange={(e) => setPassword(e.target.value)} 
                required 
                style={{ padding: '8px' }}
            />
            <button type="submit" style={{ padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
                Login
            </button>
        </form>
    );
};

export default Login;
