import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import api from './api'; 

const Chat = ({ receiverId, receiverName }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const socket = useRef(null);
    const scrollRef = useRef(null);
    const authUser = localStorage.getItem('username');

    // 1. SCROLL CONTROL: Keep the latest message in view
    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
        }
    }, [messages]);

    useEffect(() => {
        if (!authUser || !receiverName) return;

        // 2. FETCH HISTORY: Pull old messages from the database
        const fetchHistory = async () => {
            try {
                const res = await api.get(`messages/${receiverName}/`);
                const history = res.data.map(m => ({
                    sender: m.sender_username,
                    message: m.content,
                    timestamp: new Date(m.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
                }));
                setMessages(history);
                console.log("‚úÖ History synced from DB");
            } catch (err) {
                console.error("‚ùå History failed to load:", err);
            }
        };

        fetchHistory();

        // 3. WEBSOCKET SETUP: Live connection
        // The .sort() ensures 'UserA_UserB' and 'UserB_UserA' both connect to 'UserA_UserB'
        const roomName = [authUser, receiverName].sort().join("_");
        
        // Connect to the WebSocket
        socket.current = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

        socket.current.onmessage = (e) => {
            const data = JSON.parse(e.data);
            console.log("üì© Message received via WebSocket:", data);

            // Only append to state if data is valid
            if (data.message && data.sender) {
                setMessages((prev) => [...prev, { 
                    sender: data.sender, 
                    message: data.message,
                    timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
                }]);
            }
        };

        socket.current.onopen = () => console.log(`üåê WebSocket Connected: ${roomName}`);
        socket.current.onclose = () => console.warn("üîå WebSocket Disconnected");

        // Cleanup connection when switching chats
        return () => {
            if (socket.current) socket.current.close();
        };
    }, [receiverName, authUser]);

    const sendMessage = () => {
        if (input.trim() && socket.current && socket.current.readyState === WebSocket.OPEN) {
            const payload = { 
                'message': input, 
                'sender': authUser 
            };
            socket.current.send(JSON.stringify(payload));
            setInput("");
        } else {
            console.error("‚ö†Ô∏è Message failed: Socket not open or input empty.");
        }
    };

    return (
        <div style={styles.chatWrapper}>
            {/* Professional Chat Header */}
            <header style={styles.header}>
                <div style={styles.headerInfo}>
                    <div style={styles.avatar}>{receiverName[0].toUpperCase()}</div>
                    <div>
                        <h4 style={{ margin: 0, color: '#fff' }}>{receiverName}</h4>
                        <span style={styles.statusLine}>Secure Live Channel</span>
                    </div>
                </div>
            </header>

            {/* Main Message Display */}
            <div style={styles.messageArea} ref={scrollRef}>
                <AnimatePresence initial={false}>
                    {messages.map((msg, i) => {
                        const isMe = msg.sender === authUser;
                        return (
                            <motion.div 
                                key={i}
                                initial={{ opacity: 0, scale: 0.8, y: 10 }}
                                animate={{ opacity: 1, scale: 1, y: 0 }}
                                style={{
                                    ...styles.bubble,
                                    alignSelf: isMe ? 'flex-end' : 'flex-start',
                                    background: isMe ? 'linear-gradient(135deg, #6366f1, #4f46e5)' : 'rgba(255,255,255,0.07)',
                                    borderBottomRightRadius: isMe ? '4px' : '16px',
                                    borderBottomLeftRadius: isMe ? '16px' : '4px',
                                }}
                            >
                                <div style={styles.messageText}>{msg.message}</div>
                                <div style={styles.timeTag}>{msg.timestamp}</div>
                            </motion.div>
                        );
                    })}
                </AnimatePresence>
            </div>

            {/* Message Input Section */}
            <div style={styles.inputContainer}>
                <div style={styles.inputBar}>
                    <input 
                        type="text" 
                        placeholder="Type something..." 
                        style={styles.chatInput}
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                    />
                    <motion.button 
                        whileTap={{ scale: 0.8 }}
                        onClick={sendMessage} 
                        style={styles.sendBtn}
                    >
                        üöÄ
                    </motion.button>
                </div>
            </div>
        </div>
    );
};

// Modern Glassmorphism Styles
const styles = {
    chatWrapper: { display: 'flex', flexDirection: 'column', height: '100%', background: '#020617' },
    header: { padding: '15px 25px', background: 'rgba(15, 23, 42, 0.9)', backdropFilter: 'blur(10px)', borderBottom: '1px solid rgba(255,255,255,0.05)' },
    headerInfo: { display: 'flex', alignItems: 'center', gap: '15px' },
    avatar: { width: '40px', height: '40px', borderRadius: '10px', background: '#6366f1', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', color: '#fff' },
    statusLine: { fontSize: '10px', color: '#10b981', fontWeight: 'bold', textTransform: 'uppercase' },
    messageArea: { flex: 1, padding: '20px', overflowY: 'auto', display: 'flex', flexDirection: 'column', gap: '10px' },
    bubble: { padding: '10px 15px', maxWidth: '70%', color: '#fff', boxShadow: '0 4px 10px rgba(0,0,0,0.1)' },
    messageText: { fontSize: '14px' },
    timeTag: { fontSize: '8px', opacity: 0.6, textAlign: 'right', marginTop: '4px' },
    inputContainer: { padding: '20px' },
    inputBar: { display: 'flex', background: 'rgba(255,255,255,0.03)', borderRadius: '15px', padding: '5px 15px', border: '1px solid rgba(255,255,255,0.1)' },
    chatInput: { flex: 1, background: 'none', border: 'none', color: '#fff', outline: 'none', padding: '10px' },
    sendBtn: { background: 'none', border: 'none', fontSize: '20px', cursor: 'pointer' }
};

export default Chat;
