import React, { useState, useEffect, useCallback } from 'react';
import Login from './Login';
import Register from './Register';
import UserSearch from './UserSearch';
import Chat from './Chat';
import FriendsList from './FriendsList';
// import api from './api';

function App() {
  const [authUser, setAuthUser] = useState(localStorage.getItem('username'));
  const [activeChat, setActiveChat] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [invites, setInvites] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState({}); 
  const [refreshFriends, setRefreshFriends] = useState(0);

  // Status Socket & Notifications
  useEffect(() => {
    if (!authUser) return;
    const statusSocket = new WebSocket(`ws://localhost:8000/ws/status/${authUser}/`);
    statusSocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setOnlineUsers(prev => ({ ...prev, [data.user]: data.status }));
    };
    return () => statusSocket.close();
  }, [authUser]);

  const handleLogout = () => {
    localStorage.clear();
    window.location.reload();
  };

  if (!authUser) {
    return (
      <div className="auth-screen" style={styles.authScreen}>
        <div style={styles.authCard}>
          <div style={styles.logoCircle}>S</div>
          <h2 style={{ marginBottom: '8px' }}>Welcome back</h2>
          <p style={{ color: 'var(--text-muted)', marginBottom: '24px' }}>Please enter your details</p>
          {showRegister ? <Register /> : <Login setAuthUser={setAuthUser} />}
          <button onClick={() => setShowRegister(!showRegister)} style={styles.toggleBtn}>
            {showRegister ? "Already have an account? Sign in" : "Don't have an account? Sign up"}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.appContainer}>
      {/* 1. Left Rail (Navigation) */}
      <div style={styles.navRail}>
        <div style={styles.logo}>S</div>
        <div style={styles.navIcons}>
           <div style={styles.activeIcon}>üí¨</div>
           <div style={styles.inactiveIcon}>üë•</div>
           <div style={styles.inactiveIcon}>‚öôÔ∏è</div>
        </div>
        <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
      </div>

      {/* 2. Sidebar (Social) */}
      <div style={styles.sidebar}>
        <div style={styles.sidebarHeader}>
          <h2 style={{ margin: 0, fontSize: '20px' }}>Messages</h2>
        </div>

        <div style={{ padding: '0 20px' }}>
          <UserSearch onSelectUser={setActiveChat} />
        </div>

        {invites.length > 0 && (
          <div style={styles.inviteSection}>
            <span style={styles.sectionLabel}>PENDING REQUESTS ({invites.length})</span>
            {invites.map(i => (
              <div key={i.id} style={styles.inviteCard}>
                <span>{i.sender}</span>
                <button style={styles.acceptBtn} onClick={() => { /* accept logic */ }}>Accept</button>
              </div>
            ))}
          </div>
        )}

        <div style={{ padding: '20px' }}>
          <span style={styles.sectionLabel}>DIRECT MESSAGES</span>
          <FriendsList 
            onSelectUser={setActiveChat} 
            refreshTrigger={refreshFriends} 
            onlineUsers={onlineUsers} 
          />
        </div>
      </div>

      {/* 3. Main Chat View */}
      <div style={styles.chatStage}>
        {activeChat ? (
          <Chat receiverId={activeChat.id} receiverName={activeChat.username} />
        ) : (
          <div style={styles.emptyStage}>
             <div style={styles.emptyIllustration}>üí¨</div>
             <h3>Select a conversation</h3>
             <p>Choose a friend from the left to start messaging in real-time.</p>
          </div>
        )}
      </div>
    </div>
  );
}

const styles = {
  appContainer: { display: 'flex', height: '100vh', overflow: 'hidden' },
  navRail: { width: '80px', background: 'var(--primary)', display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '24px 0' },
  logo: { width: '40px', height: '40px', borderRadius: '12px', background: 'rgba(255,255,255,0.2)', color: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', fontSize: '20px', marginBottom: '40px' },
  navIcons: { flex: 1, display: 'flex', flexDirection: 'column', gap: '20px' },
  activeIcon: { fontSize: '24px', background: 'rgba(255,255,255,0.15)', padding: '10px', borderRadius: '12px' },
  inactiveIcon: { fontSize: '24px', opacity: 0.6, cursor: 'pointer' },
  sidebar: { width: '350px', background: '#fff', borderRight: '1px solid var(--border)', display: 'flex', flexDirection: 'column' },
  sidebarHeader: { padding: '24px 20px' },
  chatStage: { flex: 1, background: '#fdfdfd', display: 'flex', flexDirection: 'column' },
  sectionLabel: { fontSize: '11px', fontWeight: '700', color: 'var(--text-muted)', letterSpacing: '1px', marginBottom: '12px', display: 'block' },
  authScreen: { display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100vh', background: 'linear-gradient(135deg, #6366f1 0%, #4f46e5 100%)' },
  authCard: { background: '#fff', padding: '40px', borderRadius: '24px', width: '100%', maxWidth: '400px', textAlign: 'center', boxShadow: '0 20px 25px -5px rgba(0,0,0,0.1)' },
  logoCircle: { width: '60px', height: '60px', background: 'var(--primary)', borderRadius: '50%', margin: '0 auto 20px', color: '#fff', fontSize: '28px', fontWeight: 'bold', display: 'flex', alignItems: 'center', justifyContent: 'center' },
  toggleBtn: { background: 'none', border: 'none', color: 'var(--primary)', marginTop: '20px', fontWeight: '600' },
  emptyStage: { flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', color: 'var(--text-muted)' },
  emptyIllustration: { fontSize: '64px', marginBottom: '16px', opacity: 0.2 },
  logoutBtn: { background: 'rgba(255,255,255,0.1)', border: 'none', color: '#fff', padding: '8px 12px', borderRadius: '8px', fontSize: '12px' }
};

export default App;
