import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ setAuthUser }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
    e.preventDefault();
    try {
        // Use the 'api' instance we created earlier
        const response = await api.post('login/', { 
            username: username, // Must match exactly
            password: password  // Must match exactly
        });

        localStorage.setItem('access_token', response.data.access);
        localStorage.setItem('refresh_token', response.data.refresh);
        
        setAuthUser(username);
        alert("Login Successful!");
    } catch (error) {
        console.log("Login Error Details:", error.response.data);
        alert("Login Failed: " + (error.response.data.detail || "Invalid Credentials"));
    }
};

    return (
        <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '300px', margin: 'auto' }}>
            <h3>Login</h3>
            <input type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} required />
            <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} required />
            <button type="submit">Login</button>
        </form>
    );
};

export default Login;
