import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:8000/api/',
});

// This automatically attaches the token to every single request
api.interceptors.request.use((config) => {
    const token = localStorage.getItem('access_token'); // Ensure this key matches your login logic
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
});

export default api;
