import React, { useState } from 'react';
import axios from 'axios'; // Import raw axios for the initial login call

const Login = ({ setAuthUser }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');

        try {
            // Call the Django login endpoint
            const res = await axios.post('http://localhost:8000/api/login/', { 
                username, 
                password 
            });
            
            // Django SimpleJWT returns 'access' and 'refresh'. 
            // We save 'access' as 'access_token' for our api.js interceptor.
            localStorage.setItem('access_token', res.data.access); 
            localStorage.setItem('username', username);
            
            setAuthUser(username);
            
            // Redirect or Reload to ensure the Axios Interceptor kicks in
            window.location.reload(); 
        } catch (err) {
            console.error("Login Error:", err);
            setError("Invalid credentials. Please try again.");
        }
    };

    return (
        <div style={styles.authCard}>
            <h3>Login</h3>
            {error && <p style={{ color: 'red', fontSize: '12px' }}>{error}</p>}
            <form onSubmit={handleLogin}>
                <input 
                    type="text" 
                    placeholder="Username" 
                    onChange={e => setUsername(e.target.value)} 
                    style={styles.input}
                    required 
                />
                <input 
                    type="password" 
                    placeholder="Password" 
                    onChange={e => setPassword(e.target.value)} 
                    style={styles.input}
                    required 
                />
                <button type="submit" style={styles.button}>Login</button>
            </form>
        </div>
    );
};

const styles = {
    authCard: { padding: '20px', border: '1px solid #ddd', borderRadius: '8px', background: '#fff' },
    input: { width: '100%', padding: '10px', margin: '10px 0', boxSizing: 'border-box' },
    button: { width: '100%', padding: '10px', background: '#007bff', color: 'white', border: 'none', cursor: 'pointer' }
};

export default Login;
