import React, { useEffect, useState } from 'react';
import api from './api'; // Ensure this uses your JWT interceptor
import { motion } from 'framer-motion';

const FriendsList = ({ onSelectUser, refreshTrigger, onlineUsers }) => {
    const [friends, setFriends] = useState([]);

    // 1. Fetch friends from the authenticated endpoint
    const fetchFriends = async () => {
        try {
            const res = await api.get('friends/');
            setFriends(res.data);
        } catch (err) {
            console.error("Error fetching friends list:", err);
        }
    };

    // 2. Re-fetch whenever a new invite is accepted (refreshTrigger)
    useEffect(() => {
        fetchFriends();
    }, [refreshTrigger]);

    return (
        <div style={styles.container}>
            {friends.map((friend, index) => {
                // 3. LOGIC: Determine online status from global WebSocket state
                const isOnline = onlineUsers && onlineUsers[friend.username] === 'online';

                return (
                    <motion.div 
                        key={friend.id} 
                        // Entrance Animation: Friends slide in one-by-one
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: index * 0.1, type: 'spring', stiffness: 100 }}
                        
                        // Interactive Hover/Tap effects
                        whileHover={{ scale: 1.02, backgroundColor: "rgba(255,255,255,0.05)" }}
                        whileTap={{ scale: 0.98 }}
                        
                        style={styles.card} 
                        onClick={() => onSelectUser({ id: friend.id, username: friend.username })}
                    >
                        <div style={styles.avatarWrapper}>
                            <div style={styles.avatar}>
                                {friend.username.charAt(0).toUpperCase()}
                            </div>
                            
                            {/* 4. THE DYNAMIC STATUS DOT */}
                            <motion.div 
                                animate={{ scale: isOnline ? [1, 1.2, 1] : 1 }}
                                transition={{ repeat: Infinity, duration: 2 }}
                                style={{
                                    ...styles.statusDot,
                                    backgroundColor: isOnline ? '#10b981' : '#475569',
                                    boxShadow: isOnline ? '0 0 10px #10b981' : 'none'
                                }}
                            />
                        </div>

                        <div style={styles.info}>
                            <div style={styles.username}>{friend.username}</div>
                            <div style={{
                                ...styles.statusText, 
                                color: isOnline ? '#10b981' : '#94a3b8' 
                            }}>
                                {isOnline ? 'Active Now' : 'Offline'}
                            </div>
                        </div>
                    </motion.div>
                );
            })}

            {friends.length === 0 && (
                <div style={styles.empty}>No friends yet. Start searching!</div>
            )}
        </div>
    );
};

// ADVANCED DARK UI STYLES
const styles = {
    container: {
        marginTop: '15px',
        display: 'flex',
        flexDirection: 'column',
        gap: '8px'
    },
    card: {
        display: 'flex',
        alignItems: 'center',
        padding: '12px 16px',
        borderRadius: '16px',
        cursor: 'pointer',
        background: 'rgba(255, 255, 255, 0.03)',
        border: '1px solid rgba(255, 255, 255, 0.05)',
        transition: 'border 0.2s ease',
    },
    avatarWrapper: {
        position: 'relative',
        marginRight: '14px',
    },
    avatar: {
        width: '44px',
        height: '44px',
        borderRadius: '14px',
        background: 'linear-gradient(135deg, #6366f1, #a855f7)',
        color: '#fff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontWeight: '700',
        fontSize: '18px',
        boxShadow: '0 4px 10px rgba(0,0,0,0.2)'
    },
    statusDot: {
        width: '12px',
        height: '12px',
        borderRadius: '50%',
        border: '3px solid #0f172a', // Matches Sidebar Background
        position: 'absolute',
        bottom: '-2px',
        right: '-2px',
    },
    info: {
        flex: 1,
    },
    username: {
        fontWeight: '600',
        fontSize: '15px',
        color: '#f8fafc',
        marginBottom: '2px'
    },
    statusText: {
        fontSize: '11px',
        fontWeight: '500',
        letterSpacing: '0.3px'
    },
    empty: {
        textAlign: 'center',
        color: '#64748b',
        fontSize: '13px',
        marginTop: '20px'
    }
};

export default FriendsList;
