import React, { useState, useEffect, useCallback } from 'react';
import Login from './Login';
import Register from './Register';
import UserSearch from './UserSearch';
import Chat from './Chat';
import FriendsList from './FriendsList';
import api from './api';

function App() {
  const [authUser, setAuthUser] = useState(localStorage.getItem('username'));
  const [activeChat, setActiveChat] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [invites, setInvites] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState({}); 
  const [refreshFriends, setRefreshFriends] = useState(0);

  // 1. Request Browser Notification Permissions on Load
  useEffect(() => {
    if ("Notification" in window) {
      if (Notification.permission !== "granted" && Notification.permission !== "denied") {
        Notification.requestPermission();
      }
    }
  }, []);

  // 2. Helper function to show notifications
  const triggerNotification = useCallback((title, message) => {
    if (Notification.permission === "granted") {
      new Notification(title, {
        body: message,
        icon: "https://cdn-icons-png.flaticon.com/512/733/733585.png" // Chat icon
      });
    }
  }, []);

  // 3. Global Status WebSocket (Online/Offline Tracking)
  useEffect(() => {
    if (!authUser) return;

    // Use a specific endpoint for status tracking
    const statusSocket = new WebSocket(`ws://localhost:8000/ws/status/`);

    statusSocket.onopen = () => {
      console.log("âœ… Global Status Socket Connected");
    };

    statusSocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log("ðŸ“¡ Status Update Received:", data);

      setOnlineUsers((prev) => ({
        ...prev,
        [data.user]: data.status
      }));

      // Trigger notification when someone else comes online
      if (data.status === 'online' && data.user !== authUser) {
        triggerNotification("Friend Online", `${data.user} is now active!`);
      }
    };

    statusSocket.onclose = (e) => {
      console.log("âŒ Status Socket Disconnected. Reconnect in 5s...", e.reason);
    };

    statusSocket.onerror = (err) => {
      console.error("WebSocket Error:", err);
    };

    return () => statusSocket.close();
  }, [authUser, triggerNotification]);

  // 4. Fetch Pending Invitations
  const fetchInvites = useCallback(async () => {
    if (authUser) {
      try {
        const res = await api.get('invitations/');
        setInvites(res.data);
      } catch (err) {
        console.error("Error fetching invites:", err);
      }
    }
  }, [authUser]);

  useEffect(() => {
    fetchInvites();
    const interval = setInterval(fetchInvites, 10000); // Poll for invites every 10s
    return () => clearInterval(interval);
  }, [fetchInvites]);

  // 5. Handle Invitation Actions (Accept/Reject)
  const handleInviteAction = async (inviteId, action) => {
    try {
      await api.post('invitations/respond/', { invite_id: inviteId, action });
      setInvites((prev) => prev.filter((i) => i.id !== inviteId));

      if (action === 'accepted') {
        setRefreshFriends((prev) => prev + 1); // Trigger FriendsList update
        triggerNotification("Success", "You have a new friend!");
      }
    } catch (err) {
      console.error("Action failed:", err);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    window.location.reload();
  };

  // Auth Screen
  if (!authUser) {
    return (
      <div style={styles.authWrapper}>
        <div style={styles.authCard}>
          <h2 style={styles.brandTitle}>SocialPlatform</h2>
          {showRegister ? <Register /> : <Login setAuthUser={setAuthUser} />}
          <button onClick={() => setShowRegister(!showRegister)} style={styles.toggleLink}>
            {showRegister ? "Already have an account? Login" : "New user? Create an account"}
          </button>
        </div>
      </div>
    );
  }

  // Main Dashboard
  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <div style={styles.sidebarHeader}>
          <h3>Chats</h3>
          <div style={styles.userBadge}>
             <span>{authUser}</span>
             <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
          </div>
        </div>

        <UserSearch onSelectUser={setActiveChat} />

        {/* Pending Invites Inbox */}
        {invites.length > 0 && (
          <div style={styles.inviteBox}>
            <p style={styles.inviteTitle}>Pending Requests</p>
            {invites.map((i) => (
              <div key={i.id} style={styles.inviteRow}>
                <span>{i.sender}</span>
                <div style={styles.inviteActions}>
                  <button onClick={() => handleInviteAction(i.id, 'accepted')} style={styles.btnAccept}>Accept</button>
                  <button onClick={() => handleInviteAction(i.id, 'rejected')} style={styles.btnReject}>Skip</button>
                </div>
              </div>
            ))}
          </div>
        )}

        <hr style={styles.divider} />

        {/* Friends List */}
        <FriendsList 
          onSelectUser={setActiveChat} 
          refreshTrigger={refreshFriends} 
          onlineUsers={onlineUsers} 
        />
      </div>

      {/* Main Chat Area */}
      <div style={styles.chatArea}>
        {activeChat ? (
          <Chat 
            receiverId={activeChat.id} 
            receiverName={activeChat.username} 
          />
        ) : (
          <div style={styles.placeholder}>
             <img src="https://cdn-icons-png.flaticon.com/512/2462/2462719.png" alt="chat" style={styles.placeholderImg} />
             <h3>Select a friend to start chatting</h3>
             <p>Real-time online status and notifications are active.</p>
          </div>
        )}
      </div>
    </div>
  );
}

// Styling Object
const styles = {
  container: { display: 'flex', height: '100vh', backgroundColor: '#f4f7f6', fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif' },
  sidebar: { width: '360px', backgroundColor: '#fff', borderRight: '1px solid #e6e6e6', padding: '20px', display: 'flex', flexDirection: 'column', overflowY: 'auto' },
  sidebarHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' },
  userBadge: { display: 'flex', alignItems: 'center', gap: '10px', fontSize: '14px', fontWeight: 'bold' },
  logoutBtn: { background: '#ffeded', color: '#ff4d4d', border: 'none', padding: '5px 8px', borderRadius: '5px', cursor: 'pointer', fontSize: '12px' },
  chatArea: { flex: 1, position: 'relative', display: 'flex', flexDirection: 'column' },
  placeholder: { display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100%', color: '#999' },
  placeholderImg: { width: '120px', opacity: 0.4, marginBottom: '20px' },
  inviteBox: { backgroundColor: '#fff9e6', border: '1px solid #ffeaa7', padding: '12px', borderRadius: '8px', marginTop: '10px' },
  inviteTitle: { margin: '0 0 8px 0', fontSize: '12px', fontWeight: 'bold', color: '#d39e00' },
  inviteRow: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '5px' },
  inviteActions: { display: 'flex', gap: '5px' },
  btnAccept: { backgroundColor: '#2ecc71', color: '#fff', border: 'none', padding: '3px 8px', borderRadius: '4px', cursor: 'pointer', fontSize: '11px' },
  btnReject: { backgroundColor: '#e74c3c', color: '#fff', border: 'none', padding: '3px 8px', borderRadius: '4px', cursor: 'pointer', fontSize: '11px' },
  divider: { border: '0', borderTop: '1px solid #f0f0f0', margin: '20px 0' },
  authWrapper: { display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#f0f2f5' },
  authCard: { width: '100%', maxWidth: '400px', padding: '40px', backgroundColor: '#fff', borderRadius: '12px', boxShadow: '0 10px 25px rgba(0,0,0,0.05)' },
  brandTitle: { textAlign: 'center', color: '#007bff', marginBottom: '30px' },
  toggleLink: { background: 'none', border: 'none', color: '#007bff', width: '100%', marginTop: '15px', cursor: 'pointer', fontSize: '14px' }
};

export default App;
