import React, { useState, useEffect, useRef } from 'react';

const Chat = ({ receiverId, receiverName }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const socketRef = useRef(null);
    const currentUser = localStorage.getItem('username');

    useEffect(() => {
        // 1. Create a UNIQUE room name by sorting the two usernames
        // This ensures both users end up in the same room
        const roomArr = [currentUser, receiverName].sort();
        const roomName = `${roomArr[0]}_${roomArr[1]}`;

        // 2. Connect to WebSocket
        socketRef.current = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

        socketRef.current.onmessage = (event) => {
            const data = JSON.parse(event.data);
            // Append the new message to the state so it appears on screen
            setMessages((prev) => [...prev, { sender: data.sender, content: data.message }]);
        };

        socketRef.current.onclose = () => console.log("WebSocket Disconnected");

        return () => {
            if (socketRef.current) socketRef.current.close();
        };
    }, [receiverName, currentUser]);

    const sendMessage = (e) => {
        e.preventDefault();
        if (input.trim() && socketRef.current) {
            const messageData = {
                message: input,
                sender: currentUser,
            };
            socketRef.current.send(JSON.stringify(messageData));
            setInput(''); // Clear input after sending
        }
    };

    // In the return of Chat.js, update the main container and message mapping:

return (
    <div style={styles.chatWrapper}>
        {/* Chat Header */}
        <header style={styles.header}>
            <div style={styles.headerInfo}>
                <div style={styles.avatar}>{receiverName[0].toUpperCase()}</div>
                <div>
                    <h4 style={{ margin: 0 }}>{receiverName}</h4>
                    <span style={styles.statusLine}>â€¢ Encrypted Connection</span>
                </div>
            </div>
        </header>

        {/* Messages Container with Custom Background */}
        <div style={styles.messageArea}>
            {messages.map((msg, i) => (
                <motion.div 
                    initial={{ opacity: 0, y: 10, scale: 0.95 }}
                    animate={{ opacity: 1, y: 0, scale: 1 }}
                    key={i} 
                    style={{
                        ...styles.bubble,
                        alignSelf: msg.sender === authUser ? 'flex-end' : 'flex-start',
                        background: msg.sender === authUser ? '#6366f1' : 'rgba(255,255,255,0.05)',
                        border: msg.sender === authUser ? 'none' : '1px solid rgba(255,255,255,0.1)'
                    }}
                >
                    {msg.message}
                </motion.div>
            ))}
        </div>

        {/* Improved Input Area */}
        <div style={styles.inputContainer}>
            <div style={styles.inputBar}>
                <input 
                    type="text" 
                    placeholder="Type a message..." 
                    style={styles.chatInput}
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                />
                <button onClick={sendMessage} style={styles.sendBtn}>ðŸš€</button>
            </div>
        </div>
    </div>
);
}

const styles = {
    chatWrapper: { 
        display: 'flex', flexDirection: 'column', height: '100%', 
        background: 'radial-gradient(circle at top right, #0f172a, #020617)' 
    },
    header: { padding: '18px 25px', borderBottom: '1px solid rgba(255,255,255,0.05)', background: 'rgba(15, 23, 42, 0.8)', backdropFilter: 'blur(10px)' },
    headerInfo: { display: 'flex', alignItems: 'center', gap: '12px' },
    avatar: { width: '40px', height: '40px', borderRadius: '12px', background: '#334155', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold' },
    statusLine: { fontSize: '11px', color: '#10b981', fontWeight: '600' },
    messageArea: { flex: 1, padding: '25px', overflowY: 'auto', display: 'flex', flexDirection: 'column', gap: '12px' },
    bubble: { padding: '12px 18px', borderRadius: '18px', maxWidth: '70%', fontSize: '15px', color: '#fff', boxShadow: '0 4px 15px rgba(0,0,0,0.1)' },
    inputContainer: { padding: '20px 25px', background: 'transparent' },
    inputBar: { display: 'flex', background: 'rgba(255,255,255,0.05)', borderRadius: '15px', padding: '8px 15px', alignItems: 'center', border: '1px solid rgba(255,255,255,0.1)' },
    chatInput: { flex: 1, background: 'none', border: 'none', color: '#fff', outline: 'none', padding: '10px', fontSize: '15px' },
    sendBtn: { background: 'none', border: 'none', fontSize: '20px', cursor: 'pointer' }
};

export default Chat;
