import React, { useState } from 'react';
import Login from './Login';
import Register from './Register';
import UserSearch from './UserSearch';
import Chat from './Chat';
import FriendsList from './FriendsList';
import { AnimatePresence } from 'framer-motion';

function App() {
  const [authUser, setAuthUser] = useState(localStorage.getItem('username'));
  const [showRegister, setShowRegister] = useState(false);
  // ... other states (activeChat, etc.)

  // IF NOT LOGGED IN: Show the Auth Screen
  if (!authUser) {
    return (
      <main style={{ width: '100vw', height: '100vh', position: 'relative' }}>
        <AnimatePresence mode="wait">
          {showRegister ? (
            <Register key="reg" />
          ) : (
            <Login key="log" setAuthUser={setAuthUser} />
          )}
        </AnimatePresence>

        {/* THE TOGGLE BUTTON: Positioned at the bottom for better UX */}
        <button 
          onClick={() => setShowRegister(!showRegister)}
          style={styles.floatingToggle}
        >
          {showRegister ? "Already have an account? Sign In" : "Need an account? Sign Up"}
        </button>
      </main>
    );
  }

  // IF LOGGED IN: Show the Dashboard (use your existing dashboard code here)
  return (
    <div className="dashboard-layout">
       {/* Your 3-column dashboard code we built previously */}
    </div>
  );
}

const styles = {
  floatingToggle: {
    position: 'fixed',
    bottom: '30px',
    left: '50%',
    transform: 'translateX(-50%)',
    background: 'none',
    border: 'none',
    color: '#818cf8',
    cursor: 'pointer',
    fontWeight: '600',
    zIndex: 100,
    textDecoration: 'underline'
  }
};

export default App;
