import React, { useState } from 'react';
import Register from './Register';
import Login from './Login';
import UserSearch from './UserSearch';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    return (
        <div className="App">
            {!isLoggedIn ? (
                <div>
                    <Register />
                    <p style={{ textAlign: 'center' }}>Already have an account? Log in below:</p>
                    <Login onLoginSuccess={() => setIsLoggedIn(true)} />
                </div>
            ) : (
                <UserSearch />
            )}
        </div>
    );
}
const handleRegister = async (e) => {
    e.preventDefault();
    try {
        const response = await api.post('register/', formData);
        alert("Success: " + response.data.message);
    } catch (error) {
        // Safe way to check for errors without crashing
        if (error.response) {
            // The server responded with a status code (e.g., 400, 500)
            alert("Error: " + (error.response.data.error || "Validation failed"));
        } else if (error.request) {
            // The request was made but no response was received
            alert("Error: No response from server. Is Django running?");
        } else {
            // Something else happened while setting up the request
            alert("Error: " + error.message);
        }
    }
};

export default App;
