import React, { useState, useEffect, useCallback } from 'react';
import Login from './Login';
import Register from './Register';
import UserSearch from './UserSearch';
import Chat from './Chat';
import FriendsList from './FriendsList';
import api from './api';

function App() {
  const [authUser, setAuthUser] = useState(localStorage.getItem('username'));
  const [activeChat, setActiveChat] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [invites, setInvites] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState({}); 
  const [refreshFriends, setRefreshFriends] = useState(0);

  useEffect(() => {
    if (!authUser) return;
    const statusSocket = new WebSocket(`ws://localhost:8000/ws/status/${authUser}/`);
    statusSocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setOnlineUsers(prev => ({ ...prev, [data.user]: data.status }));
    };
    return () => statusSocket.close();
  }, [authUser]);

  const handleLogout = () => {
    localStorage.clear();
    window.location.reload();
  };

  if (!authUser) {
    return (
      <div style={styles.authScreen}>
        <div style={styles.authCard}>
          <div style={styles.logoCircle}>S</div>
          <h2 style={{ color: '#fff' }}>SocialPlatform</h2>
          <p style={{ color: 'var(--text-muted)' }}>Sign in to continue</p>
          {showRegister ? <Register /> : <Login setAuthUser={setAuthUser} />}
          <button onClick={() => setShowRegister(!showRegister)} style={styles.toggleBtn}>
            {showRegister ? "Already have an account? Sign in" : "Create new account"}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.appContainer}>
      {/* 1. Sidebar (Dark Slate) */}
      <div style={styles.sidebar}>
        <div style={styles.sidebarHeader}>
          <div style={styles.profileRow}>
            <div style={styles.avatarSmall}>{authUser[0].toUpperCase()}</div>
            <span style={{ fontWeight: 'bold' }}>{authUser}</span>
            <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
          </div>
          <h2 style={{ fontSize: '24px', marginTop: '20px' }}>Chats</h2>
        </div>

        <div style={{ padding: '0 20px' }}>
          <UserSearch onSelectUser={setActiveChat} />
        </div>

        <div style={styles.scrollArea}>
          {invites.length > 0 && (
            <div style={styles.section}>
              <span style={styles.sectionLabel}>INVITATIONS</span>
              {invites.map(i => (
                <div key={i.id} style={styles.inviteCard}>
                  <span>{i.sender}</span>
                  <button style={styles.acceptBtn}>Accept</button>
                </div>
              ))}
            </div>
          )}

          <div style={styles.section}>
            <span style={styles.sectionLabel}>FRIENDS</span>
            <FriendsList 
              onSelectUser={setActiveChat} 
              refreshTrigger={refreshFriends} 
              onlineUsers={onlineUsers} 
            />
          </div>
        </div>
      </div>

      {/* 2. Main Chat View (Deep Background) */}
      <div style={styles.chatStage}>
        {activeChat ? (
          <Chat receiverId={activeChat.id} receiverName={activeChat.username} />
        ) : (
          <div style={styles.emptyStage}>
             <div style={styles.emptyIcon}>âœ¨</div>
             <h3>Start a new conversation</h3>
             <p>Select a friend from the sidebar to chat</p>
          </div>
        )}
      </div>
    </div>
  );
}

const styles = {
  appContainer: { display: 'flex', height: '100vh', backgroundColor: 'var(--bg-app)', color: 'var(--text-main)' },
  sidebar: { width: '360px', backgroundColor: 'var(--bg-sidebar)', borderRight: '1px solid var(--border)', display: 'flex', flexDirection: 'column' },
  sidebarHeader: { padding: '24px 20px' },
  profileRow: { display: 'flex', alignItems: 'center', gap: '12px' },
  avatarSmall: { width: '32px', height: '32px', borderRadius: '8px', background: 'var(--primary)', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold' },
  logoutBtn: { marginLeft: 'auto', background: '#ef4444', border: 'none', color: '#fff', padding: '4px 8px', borderRadius: '6px', fontSize: '11px', cursor: 'pointer' },
  scrollArea: { flex: 1, overflowY: 'auto', padding: '10px 20px' },
  section: { marginBottom: '25px' },
  sectionLabel: { fontSize: '10px', fontWeight: '800', color: 'var(--text-muted)', letterSpacing: '1.5px', marginBottom: '15px', display: 'block' },
  chatStage: { flex: 1, backgroundColor: 'var(--bg-app)', position: 'relative' },
  emptyStage: { height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', color: 'var(--text-muted)' },
  emptyIcon: { fontSize: '50px', marginBottom: '20px', opacity: 0.3 },
  authScreen: { display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100vh', backgroundColor: '#020617' },
  authCard: { background: 'var(--bg-sidebar)', padding: '40px', borderRadius: '24px', width: '360px', textAlign: 'center', border: '1px solid var(--border)' },
  logoCircle: { width: '50px', height: '50px', background: 'var(--primary)', borderRadius: '15px', margin: '0 auto 15px', color: '#fff', fontSize: '24px', fontWeight: 'bold', display: 'flex', alignItems: 'center', justifyContent: 'center', boxShadow: '0 0 20px rgba(129, 140, 248, 0.4)' },
  toggleBtn: { background: 'none', border: 'none', color: 'var(--primary)', marginTop: '20px', cursor: 'pointer' }
};

export default App;
