import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Chat = ({ receiverId, receiverName }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const socket = useRef(null);
    const scrollRef = useRef(null);
    const authUser = localStorage.getItem('username');

    // Auto-scroll to bottom when new messages arrive
    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
        }
    }, [messages]);

    useEffect(() => {
        const roomName = [authUser, receiverName].sort().join("_");
        socket.current = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

        socket.current.onmessage = (e) => {
            const data = JSON.parse(e.data);
            setMessages((prev) => [...prev, { sender: data.sender, message: data.message }]);
        };

        return () => socket.current.close();
    }, [receiverName, authUser]);

    const sendMessage = () => {
        if (input.trim() && socket.current) {
            socket.current.send(JSON.stringify({ 'message': input, 'sender': authUser }));
            setInput("");
        }
    };

    return (
        <div style={styles.chatWrapper}>
            {/* 1. Header with Blur Effect */}
            <header style={styles.header}>
                <div style={styles.headerInfo}>
                    <div style={styles.avatar}>{receiverName[0].toUpperCase()}</div>
                    <div>
                        <h4 style={{ margin: 0, color: '#fff' }}>{receiverName}</h4>
                        <span style={styles.statusLine}>â€¢ Secure Channel</span>
                    </div>
                </div>
            </header>

            {/* 2. Message Area with Radial Gradient */}
            <div style={styles.messageArea} ref={scrollRef}>
                <AnimatePresence>
                    {messages.map((msg, i) => {
                        const isMe = msg.sender === authUser;
                        return (
                            <motion.div 
                                key={i}
                                initial={{ opacity: 0, y: 15, scale: 0.9 }}
                                animate={{ opacity: 1, y: 0, scale: 1 }}
                                transition={{ type: 'spring', stiffness: 260, damping: 20 }}
                                style={{
                                    ...styles.bubble,
                                    alignSelf: isMe ? 'flex-end' : 'flex-start',
                                    background: isMe ? 'var(--primary)' : 'rgba(255,255,255,0.08)',
                                    borderBottomRightRadius: isMe ? '4px' : '20px',
                                    borderBottomLeftRadius: isMe ? '20px' : '4px',
                                }}
                            >
                                <div style={styles.messageText}>{msg.message}</div>
                                <div style={styles.timeTag}>{new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</div>
                            </motion.div>
                        );
                    })}
                </AnimatePresence>
            </div>

            {/* 3. Floating Input Bar */}
            <div style={styles.inputContainer}>
                <div style={styles.inputBar}>
                    <input 
                        type="text" 
                        placeholder="Message..." 
                        style={styles.chatInput}
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                    />
                    <motion.button 
                        whileHover={{ scale: 1.1 }}
                        whileTap={{ scale: 0.9 }}
                        onClick={sendMessage} 
                        style={styles.sendBtn}
                    >
                        ðŸš€
                    </motion.button>
                </div>
            </div>
        </div>
    );
};

// Styles for the Professional Look
const styles = {
    chatWrapper: { 
        display: 'flex', 
        flexDirection: 'column', 
        height: '100%', 
        background: 'radial-gradient(circle at top right, #1e293b, #020617)',
        position: 'relative'
    },
    header: { 
        padding: '16px 24px', 
        background: 'rgba(15, 23, 42, 0.7)', 
        backdropFilter: 'blur(12px)', 
        borderBottom: '1px solid rgba(255,255,255,0.05)',
        zIndex: 10
    },
    headerInfo: { display: 'flex', alignItems: 'center', gap: '14px' },
    avatar: { 
        width: '42px', height: '42px', borderRadius: '14px', 
        background: 'linear-gradient(135deg, #6366f1, #a855f7)', 
        display: 'flex', alignItems: 'center', justifyContent: 'center', 
        fontWeight: 'bold', color: '#fff' 
    },
    statusLine: { fontSize: '11px', color: '#10b981', fontWeight: '600', letterSpacing: '0.5px' },
    messageArea: { 
        flex: 1, padding: '24px', overflowY: 'auto', display: 'flex', 
        flexDirection: 'column', gap: '10px', scrollBehavior: 'smooth' 
    },
    bubble: { 
        padding: '12px 16px', borderRadius: '20px', maxWidth: '65%', 
        color: '#fff', boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
        position: 'relative'
    },
    messageText: { fontSize: '15px', lineHeight: '1.4' },
    timeTag: { fontSize: '9px', opacity: 0.6, textAlign: 'right', marginTop: '4px' },
    inputContainer: { padding: '20px 24px', background: 'transparent' },
    inputBar: { 
        display: 'flex', background: 'rgba(255,255,255,0.05)', 
        borderRadius: '18px', padding: '10px 18px', alignItems: 'center', 
        border: '1px solid rgba(255,255,255,0.1)',
        backdropFilter: 'blur(5px)'
    },
    chatInput: { 
        flex: 1, background: 'none', border: 'none', color: '#fff', 
        outline: 'none', padding: '8px', fontSize: '15px' 
    },
    sendBtn: { background: 'none', border: 'none', fontSize: '22px', cursor: 'pointer' }
};

export default Chat; // ONLY ONE EXPORT
