import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import api from './api'; 

const Chat = ({ receiverId, receiverName }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const socket = useRef(null);
    const scrollRef = useRef(null);

    // FIX: Moved this up so it exists before any logic runs
    const authUser = localStorage.getItem('username');

    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
        }
    }, [messages]);

    useEffect(() => {
        // Now authUser is properly initialized and accessible here
        if (!authUser || !receiverName) return;

        const fetchHistory = async () => {
            try {
                const res = await api.get(`messages/${receiverName}/`);
                const history = res.data.map(m => ({
                    sender: m.sender_username,
                    message: m.content,
                    timestamp: new Date(m.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
                }));
                setMessages(history);
            } catch (err) {
                console.error("History error:", err);
            }
        };

        fetchHistory();

        // Safe to use authUser here for the room name
        const roomName = [authUser, receiverName].sort().join("_");
        socket.current = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

        socket.current.onmessage = (e) => {
            const data = JSON.parse(e.data);
            if (data.message && data.sender) {
                setMessages((prev) => [...prev, { 
                    sender: data.sender, 
                    message: data.message,
                    timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
                }]);
            }
        };

        return () => {
            if (socket.current) socket.current.close();
        };
    }, [receiverName, authUser]);

    const sendMessage = () => {
        if (input.trim() && socket.current?.readyState === WebSocket.OPEN) {
            socket.current.send(JSON.stringify({ 'message': input, 'sender': authUser }));
            setInput("");
        }
    };

    return (
        <div style={styles.chatWrapper}>
            <header style={styles.header}>
                <div style={styles.headerInfo}>
                    <div style={styles.avatar}>{receiverName ? receiverName[0].toUpperCase() : "?"}</div>
                    <h4 style={{ margin: 0, color: '#fff' }}>{receiverName}</h4>
                </div>
            </header>

            <div style={styles.messageArea} ref={scrollRef}>
                {messages.map((msg, i) => (
                    <div key={i} style={{
                        ...styles.bubble,
                        alignSelf: msg.sender === authUser ? 'flex-end' : 'flex-start',
                        background: msg.sender === authUser ? '#4f46e5' : '#1e293b'
                    }}>
                        {msg.message}
                    </div>
                ))}
            </div>

            <div style={styles.inputContainer}>
                <input 
                    style={styles.chatInput}
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                />
                <button onClick={sendMessage}>ðŸš€</button>
            </div>
        </div>
    );
};

const styles = {
    chatWrapper: { display: 'flex', flexDirection: 'column', height: '100%', background: '#020617' },
    header: { padding: '15px', background: '#0f172a', borderBottom: '1px solid #334155' },
    headerInfo: { display: 'flex', alignItems: 'center', gap: '10px' },
    avatar: { width: '35px', height: '35px', borderRadius: '50%', background: '#6366f1', display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#fff' },
    messageArea: { flex: 1, padding: '20px', overflowY: 'auto', display: 'flex', flexDirection: 'column', gap: '10px' },
    bubble: { padding: '10px 15px', borderRadius: '15px', color: '#fff', maxWidth: '70%' },
    inputContainer: { padding: '20px', display: 'flex', gap: '10px' },
    chatInput: { flex: 1, padding: '10px', borderRadius: '5px', border: '1px solid #334155', background: '#0f172a', color: '#fff' }
};

export default Chat;
