import React, { useState } from 'react';
import api from './api'; // Using our 'api.js' instance for consistency

const Register = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: ''
    });

    const handleRegister = async (e) => {
        e.preventDefault();
        try {
            // Sending the POST request to Django
            const response = await api.post('register/', formData);
            alert("Success: " + response.data.message);
            // After success, you can redirect the user to the login page
        } catch (error) {
            console.error("Registration failed:", error.response.data);
            alert("Error: " + (error.response.data.error || "Something went wrong"));
        }
    };

    return (
        <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '10px', maxWidth: '300px', margin: '20px auto' }}>
            <h3>Create Account</h3>
            <form onSubmit={handleRegister} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                <input 
                    type="text" 
                    placeholder="Username" 
                    onChange={(e) => setFormData({...formData, username: e.target.value})} 
                    required 
                />
                <input 
                    type="email" 
                    placeholder="Email" 
                    onChange={(e) => setFormData({...formData, email: e.target.value})} 
                    required 
                />
                <input 
                    type="password" 
                    placeholder="Password" 
                    onChange={(e) => setFormData({...formData, password: e.target.value})} 
                    required 
                />
                <button type="submit" style={{ backgroundColor: '#28a745', color: 'white', border: 'none', padding: '10px', cursor: 'pointer' }}>
                    Sign Up
                </button>
            </form>
        </div>
    );
};

export default Register;
