import React, { useState, useEffect } from 'react';
import api from './api'; 

const FriendsList = ({ onSelectFriend, selectedFriendId }) => {
    const [friends, setFriends] = useState([]);
    const [loading, setLoading] = useState(true);

    // 1. Fetch friends from your Django 'api/friends/' endpoint
    useEffect(() => {
        const fetchFriends = async () => {
            try {
                const res = await api.get('friends/');
                setFriends(res.data);
                setLoading(false);
            } catch (err) {
                console.error("❌ API Error: Could not fetch friends list", err);
                setLoading(false);
            }
        };

        fetchFriends();
    }, []);

    if (loading) return <div style={styles.statusMsg}>Loading chats...</div>;

    return (
        <div style={styles.listContainer}>
            <h4 style={styles.sectionTitle}>Recent Conversations</h4>
            
            <div style={styles.scrollArea}>
                {friends.length === 0 ? (
                    <div style={styles.emptyMsg}>No friends found. Search for a user to start.</div>
                ) : (
                    friends.map((friend) => {
                        const isSelected = selectedFriendId === friend.id;
                        return (
                            <div 
                                key={friend.id} 
                                onClick={() => onSelectFriend(friend)}
                                style={{
                                    ...styles.friendRow,
                                    backgroundColor: isSelected ? 'rgba(99, 102, 241, 0.15)' : 'transparent',
                                    borderLeft: isSelected ? '4px solid #6366f1' : '4px solid transparent'
                                }}
                            >
                                <div style={styles.avatar}>
                                    {friend.username[0].toUpperCase()}
                                </div>
                                <div style={styles.details}>
                                    <div style={styles.name}>{friend.username}</div>
                                    <div style={styles.onlineStatus}>Available</div>
                                </div>
                            </div>
                        );
                    })
                )}
            </div>
        </div>
    );
};

// --- MODERN DARK THEME STYLES ---
const styles = {
    listContainer: { display: 'flex', flexDirection: 'column', height: '100%', background: '#0f172a' },
    sectionTitle: { color: '#64748b', fontSize: '11px', textTransform: 'uppercase', padding: '15px 24px', letterSpacing: '1px', margin: 0 },
    scrollArea: { flex: 1, overflowY: 'auto' },
    friendRow: { 
        display: 'flex', 
        alignItems: 'center', 
        padding: '14px 24px', 
        cursor: 'pointer', 
        gap: '12px',
        transition: 'background 0.2s ease'
    },
    avatar: { 
        width: '42px', 
        height: '42px', 
        borderRadius: '12px', 
        background: '#4f46e5', 
        color: '#fff', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center', 
        fontWeight: 'bold',
        fontSize: '16px'
    },
    details: { display: 'flex', flexDirection: 'column' },
    name: { color: '#f8fafc', fontWeight: '600', fontSize: '14px' },
    onlineStatus: { color: '#10b981', fontSize: '11px', marginTop: '2px' },
    statusMsg: { color: '#64748b', textAlign: 'center', padding: '20px', fontSize: '13px' },
    emptyMsg: { color: '#475569', textAlign: 'center', padding: '40px 20px', fontSize: '13px' }
};

export default FriendsList;
