import React, { useState } from 'react';
import api from './api';

const UserSearch = ({ onSelectUser }) => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);

    // Inside UserSearch.js
const handleSearch = async (val) => {
    setQuery(val);
    if (val.length > 1) {
        try {
            const res = await api.get(`users/?search=${val}`);
            setResults(res.data);
        } catch (err) {
            if (err.response && err.response.status === 401) {
                console.error("Session expired. Please login again.");
                // Optionally: localStorage.clear(); window.location.reload();
            }
        }
    }
};

    const sendInvite = async (userId) => {
    try {
        const res = await api.post('invitations/send/', { receiver_id: userId });
        alert(res.data.message);
    } catch (err) {
        // This will catch the "Already friends" message from Django
        alert(err.response?.data?.message || "Invitation error");
    }
};

    return (
        <div>
            <h4>Find Users</h4>
            <input 
                type="text" 
                placeholder="Search username..." 
                value={query} 
                onChange={(e) => handleSearch(e.target.value)}
                style={{ width: '100%', padding: '8px', marginBottom: '15px' }}
            />
            {results.map(user => (
    <div key={user.id} style={{ display: 'flex', justifyContent: 'space-between', padding: '10px 0' }}>
        <span>{user.username}</span>
        <div>
            {user.status === 'none' && (
                <button onClick={() => sendInvite(user.id)}>Invite</button>
            )}
            {user.status === 'pending' && (
                <span style={{ fontSize: '11px', color: '#888' }}>Pending...</span>
            )}
            {user.status === 'accepted' && (
                <span style={{ fontSize: '11px', color: 'green' }}>âœ“ Friend</span>
            )}
        </div>
    </div>
))}
        </div>
    );
};

export default UserSearch;
