import React, { useState } from 'react';
import api from './api';

const UserSearch = ({ onSelectUser }) => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);

    const handleSearch = async (val) => {
        setQuery(val);
        if (val.length > 1) {
            const res = await api.get(`users/?search=${val}`);
            setResults(res.data);
        }
    };

    const sendInvite = async (userId) => {
        try {
            await api.post('invitations/send/', { receiver_id: userId });
            alert("Invite Sent!");
        } catch (err) {
            alert(err.response?.data?.message || "Already sent or error occurred");
        }
    };

    return (
        <div>
            <h4>Find Users</h4>
            <input 
                type="text" 
                placeholder="Search username..." 
                value={query} 
                onChange={(e) => handleSearch(e.target.value)}
                style={{ width: '100%', padding: '8px', marginBottom: '15px' }}
            />
            {results.map(user => (
                <div key={user.id} style={{ display: 'flex', justifyContent: 'space-between', padding: '10px', borderBottom: '1px solid #eee' }}>
                    <span>{user.username}</span>
                    {/* Logic Note: In a full app, the backend would return 'is_friend' status.
                        For now, we provide both buttons or just the Invite.
                    */}
                    <div>
                        <button onClick={() => sendInvite(user.id)} style={{ marginRight: '5px', fontSize: '11px' }}>Invite</button>
                        <button onClick={() => onSelectUser(user)} style={{ fontSize: '11px', background: '#007bff', color: 'white', border: 'none' }}>Chat</button>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default UserSearch;
