import React, { useEffect, useState } from 'react';
import api from './api';
import { motion } from 'framer-motion'; // <--- Add this line

const FriendsList = ({ onSelectUser, refreshTrigger, onlineUsers }) => {
    const [friends, setFriends] = useState([]);

    const fetchFriends = async () => {
        try {
            const res = await api.get('friends/');
            setFriends(res.data);
        } catch (err) {
            console.error("Error fetching friends", err);
        }
    };

    useEffect(() => {
        fetchFriends();
    }, [refreshTrigger]);

    return (
        <div style={styles.container}>
            {friends.map((friend) => {
                // Inside the return map of FriendsList.js
<motion.div 
    whileHover={{ scale: 1.02, backgroundColor: "rgba(255,255,255,0.05)" }}
    whileTap={{ scale: 0.98 }}
    key={friend.id}
    style={styles.friendItem}
    onClick={() => onSelectUser(friend)}
>
    <div style={styles.avatar}>
        {friend.username[0].toUpperCase()}
        <div style={{
            ...styles.dot, 
            backgroundColor: onlineUsers[friend.username] === 'online' ? 'var(--online)' : '#475569'
        }} />
    </div>
    <div style={styles.meta}>
        <span style={styles.name}>{friend.username}</span>
        <span style={styles.sub}>{onlineUsers[friend.username] === 'online' ? 'Active' : 'Offline'}</span>
    </div>
</motion.div>
                // Here we check the onlineUsers prop passed from App.js
                const isOnline = onlineUsers && onlineUsers[friend.username] === 'online';

                return (
                    <div 
                        key={friend.id} 
                        style={styles.card} 
                        onClick={() => onSelectUser({ id: friend.id, username: friend.username })}
                        onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#f8fafc'}
                        onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
                    >
                        <div style={styles.avatarWrapper}>
                            <div style={styles.avatar}>
                                {friend.username.charAt(0).toUpperCase()}
                            </div>
                            {/* The Status Dot */}
                            <div style={{
                                ...styles.statusDot,
                                backgroundColor: isOnline ? '#22c55e' : '#cbd5e1'
                            }}></div>
                        </div>

                        <div style={styles.info}>
                            <div style={styles.username}>{friend.username}</div>
                            <div style={styles.statusText}>
                                {isOnline ? 'Active now' : 'Offline'}
                            </div>
                        </div>
                    </div>
                );
            })}
            
            {friends.length === 0 && (
                <p style={styles.emptyText}>No friends connected yet.</p>
            )}
        </div>
    );
};

// Portfolio-ready Styles
// src/FriendsList.js Styling Section
const styles = {
    card: {
        display: 'flex',
        alignItems: 'center',
        padding: '12px',
        borderRadius: '14px',
        cursor: 'pointer',
        transition: 'all 0.2s ease',
        marginBottom: '6px',
        border: '1px solid transparent',
    },
    avatar: {
        width: '44px',
        height: '44px',
        borderRadius: '14px',
        backgroundColor: '#334155', // Slightly lighter slate
        color: 'var(--primary)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontWeight: 'bold',
        fontSize: '18px',
        border: '1px solid var(--border)'
    },
    username: {
        fontWeight: '600',
        fontSize: '15px',
        color: '#f1f5f9', // Near white
    },
    statusText: {
        fontSize: '11px',
        color: 'var(--text-muted)',
    },
    statusDot: {
        width: '12px',
        height: '12px',
        borderRadius: '50%',
        border: '2px solid var(--bg-sidebar)',
        position: 'absolute',
        bottom: '-2px',
        right: '-2px',
        boxShadow: '0 0 8px rgba(0,0,0,0.5)'
    }
};

// Logic to apply hover effect in Dark Mode:
// onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#2d3748'}
// onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'transparent'}

export default FriendsList;
