import React, { useState, useEffect, useRef } from 'react';

const Chat = ({ receiverId, receiverName }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const socketRef = useRef(null);
    const currentUser = localStorage.getItem('username');

    useEffect(() => {
        // 1. Create a UNIQUE room name by sorting the two usernames
        // This ensures both users end up in the same room
        const roomArr = [currentUser, receiverName].sort();
        const roomName = `${roomArr[0]}_${roomArr[1]}`;

        // 2. Connect to WebSocket
        socketRef.current = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

        socketRef.current.onmessage = (event) => {
            const data = JSON.parse(event.data);
            // Append the new message to the state so it appears on screen
            setMessages((prev) => [...prev, { sender: data.sender, content: data.message }]);
        };

        socketRef.current.onclose = () => console.log("WebSocket Disconnected");

        return () => {
            if (socketRef.current) socketRef.current.close();
        };
    }, [receiverName, currentUser]);

    const sendMessage = (e) => {
        e.preventDefault();
        if (input.trim() && socketRef.current) {
            const messageData = {
                message: input,
                sender: currentUser,
            };
            socketRef.current.send(JSON.stringify(messageData));
            setInput(''); // Clear input after sending
        }
    };

    return (
        <div style={{ border: '1px solid #ccc', borderRadius: '8px', padding: '15px', height: '400px', display: 'flex', flexDirection: 'column' }}>
            <h4>Chatting with {receiverName}</h4>
            
            <div style={{ flex: '1', overflowY: 'auto', marginBottom: '10px', padding: '10px', backgroundColor: '#f9f9f9' }}>
                {messages.map((msg, index) => (
                    <div key={index} style={{ 
                        textAlign: msg.sender === currentUser ? 'right' : 'left',
                        margin: '5px 0'
                    }}>
                        <span style={{ 
                            padding: '8px 12px', 
                            borderRadius: '15px', 
                            display: 'inline-block',
                            backgroundColor: msg.sender === currentUser ? '#007bff' : '#e9ecef',
                            color: msg.sender === currentUser ? 'white' : 'black'
                        }}>
                            {msg.content}
                        </span>
                    </div>
                ))}
            </div>

            <form onSubmit={sendMessage} style={{ display: 'flex', gap: '5px' }}>
                <input 
                    type="text" 
                    value={input} 
                    onChange={(e) => setInput(e.target.value)} 
                    placeholder="Type a message..."
                    style={{ flex: '1', padding: '8px' }}
                />
                <button type="submit" style={{ padding: '8px 15px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px' }}>
                    Send
                </button>
            </form>
        </div>
    );
};

export default Chat;
