import React, { useEffect, useState } from 'react';
import api from './api';

const FriendsList = ({ onSelectUser, refreshTrigger, onlineUsers }) => {
    const [friends, setFriends] = useState([]);

    const fetchFriends = async () => {
        try {
            const res = await api.get('friends/');
            setFriends(res.data);
        } catch (err) { console.error("Error fetching friends"); }
    };

    useEffect(() => {
        fetchFriends();
    }, [refreshTrigger]);

    const handleUnfriend = async (inviteId) => {
        if (window.confirm("Remove this friend?")) {
            await api.delete(`friends/remove/${inviteId}/`);
            setFriends(prev => prev.filter(f => f.invite_id !== inviteId));
            onSelectUser(null);
        }
    };

    return (
        <div style={{ marginTop: '20px' }}>
            <h4 style={{ color: '#555', marginBottom: '10px' }}>Your Friends</h4>
            {friends.map(friend => {
                const isOnline = onlineUsers[friend.username] === 'online';
                
                return (
                    <div key={friend.id} style={styles.friendCard}>
                        <div 
                            style={styles.clickable} 
                            onClick={() => onSelectUser({ id: friend.id, username: friend.username })}
                        >
                            <div style={styles.avatarWrapper}>
                                <span style={{ fontSize: '20px' }}>ðŸ‘¤</span>
                                {/* Online/Offline Dot */}
                                <div style={{
                                    ...styles.statusDot,
                                    backgroundColor: isOnline ? '#2ecc71' : '#bdc3c7'
                                }}></div>
                            </div>
                            <span style={{ 
                                fontWeight: 'bold', 
                                color: isOnline ? '#2c3e50' : '#7f8c8d' 
                            }}>
                                {friend.username}
                            </span>
                        </div>
                        <button onClick={() => handleUnfriend(friend.invite_id)} style={styles.removeLink}>Remove</button>
                    </div>
                );
            })}
            {friends.length === 0 && <p style={{ fontSize: '12px', color: '#999' }}>No friends yet.</p>}
        </div>
    );
};

// src/FriendsList.js
const styles = {
  card: {
    display: 'flex',
    alignItems: 'center',
    padding: '12px',
    borderRadius: '12px',
    marginBottom: '8px',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    border: '1px solid transparent'
  },
  cardHover: {
    background: '#f1f5f9',
  },
  avatar: {
    width: '44px',
    height: '44px',
    borderRadius: '14px',
    background: '#e2e8f0',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '20px',
    position: 'relative',
    marginRight: '12px'
  },
  statusDot: {
    width: '12px',
    height: '12px',
    borderRadius: '50%',
    border: '2px solid #fff',
    position: 'absolute',
    bottom: '-2px',
    right: '-2px'
  }
};

// Inside the component return:
<div 
  key={friend.id} 
  style={styles.card} 
  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#f1f5f9'}
  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
  onClick={() => onSelectUser(friend)}
>
  <div style={styles.avatar}>
    ðŸ‘¤
    <div style={{...styles.statusDot, backgroundColor: onlineUsers[friend.username] === 'online' ? 'var(--online)' : '#cbd5e1'}}></div>
  </div>
  <div style={{ flex: 1 }}>
    <div style={{ fontWeight: '600', fontSize: '15px' }}>{friend.username}</div>
    <div style={{ fontSize: '12px', color: 'var(--text-muted)' }}>
      {onlineUsers[friend.username] === 'online' ? 'Active now' : 'Offline'}
    </div>
  </div>
</div>

export default FriendsList;
