import React, { useState, useEffect, useCallback } from 'react';
import Login from './Login';
import Register from './Register';
import UserSearch from './UserSearch';
import Chat from './Chat';
import FriendsList from './FriendsList';
import api from './api';

function App() {
  const [authUser, setAuthUser] = useState(localStorage.getItem('username'));
  const [activeChat, setActiveChat] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [invites, setInvites] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState({}); 
  const [refreshFriends, setRefreshFriends] = useState(0); // This triggers the Friends List update

  // 1. Request Browser Notifications
  useEffect(() => {
    if ("Notification" in window && Notification.permission === "default") {
      Notification.requestPermission();
    }
  }, []);

  // 2. Global Status Socket (Presence & Notifications)
  useEffect(() => {
    if (!authUser) {
      setOnlineUsers({});
      return;
    }

    // Connect using the username in the URL to avoid 401/Anonymous issues
    const statusSocket = new WebSocket(`ws://localhost:8000/ws/status/${authUser}/`);

    statusSocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setOnlineUsers((prev) => ({
        ...prev,
        [data.user]: data.status
      }));

      // Notify if someone else comes online
      if (data.status === 'online' && data.user !== authUser) {
        if (Notification.permission === "granted") {
          new Notification("Social App", { body: `${data.user} is now active!` });
        }
      }
    };

    return () => statusSocket.close();
  }, [authUser]);

  // 3. Fetch Pending Invitations
  const fetchInvites = useCallback(async () => {
    if (authUser) {
      try {
        const res = await api.get('invitations/');
        setInvites(res.data);
      } catch (err) {
        console.error("Invite fetch error", err);
      }
    }
  }, [authUser]);

  useEffect(() => {
    fetchInvites();
    const interval = setInterval(fetchInvites, 10000);
    return () => clearInterval(interval);
  }, [fetchInvites]);

  // 4. Handle Accepting Invitations (Crucial for Friends List)
  const handleInviteAction = async (inviteId, action) => {
    try {
      await api.post('invitations/respond/', { invite_id: inviteId, action });
      setInvites((prev) => prev.filter((i) => i.id !== inviteId));

      if (action === 'accepted') {
        // INCREMENTING THIS REFRESHES THE FRIENDSLIST COMPONENT
        setRefreshFriends((prev) => prev + 1); 
      }
    } catch (err) {
      console.error("Invite response failed", err);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    setAuthUser(null);
    setActiveChat(null);
    window.location.reload();
  };

  if (!authUser) {
    return (
      <div style={styles.authWrapper}>
        <div style={styles.authCard}>
          <h2 style={{ textAlign: 'center', color: '#007bff' }}>SocialPlatform</h2>
          {showRegister ? <Register /> : <Login setAuthUser={setAuthUser} />}
          <button onClick={() => setShowRegister(!showRegister)} style={styles.toggleLink}>
            {showRegister ? "Back to Login" : "Create Account"}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.layout}>
      {/* Sidebar: Social & Connections */}
      <div style={styles.sidebar}>
        <div style={styles.sidebarHeader}>
          <h3>Platform</h3>
          <div style={styles.userSection}>
            <span><b>{authUser}</b></span>
            <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
          </div>
        </div>

        {/* Search Component */}
        <UserSearch onSelectUser={setActiveChat} />

        {/* Invitations Inbox */}
        {invites.length > 0 && (
          <div style={styles.inviteBox}>
            <p style={{ margin: '0 0 10px 0', fontSize: '13px', fontWeight: 'bold' }}>ðŸ”” New Requests</p>
            {invites.map((i) => (
              <div key={i.id} style={styles.inviteRow}>
                <span>{i.sender}</span>
                <div style={styles.inviteActions}>
                  <button onClick={() => handleInviteAction(i.id, 'accepted')} style={styles.btnAccept}>Accept</button>
                  <button onClick={() => handleInviteAction(i.id, 'rejected')} style={styles.btnReject}>Skip</button>
                </div>
              </div>
            ))}
          </div>
        )}

        <hr style={{ border: '0.5px solid #f0f0f0', margin: '20px 0' }} />

        {/* RESTORED: Friends List Section */}
        <FriendsList 
          onSelectUser={setActiveChat} 
          refreshTrigger={refreshFriends} // List updates when this changes
          onlineUsers={onlineUsers}       // List shows dots using this
        />
      </div>

      {/* Main: Chat View */}
      <div style={styles.mainContent}>
        {activeChat ? (
          <Chat 
            receiverId={activeChat.id} 
            receiverName={activeChat.username} 
          />
        ) : (
          <div style={styles.emptyState}>
            <img src="https://cdn-icons-png.flaticon.com/512/2462/2462719.png" alt="welcome" style={{ width: '80px', opacity: 0.3 }} />
            <h3>Your Conversations</h3>
            <p>Select a friend to begin chatting.</p>
          </div>
        )}
      </div>
    </div>
  );
}

const styles = {
  layout: { display: 'flex', height: '100vh', backgroundColor: '#f8f9fa' },
  sidebar: { width: '360px', backgroundColor: '#fff', borderRight: '1px solid #ddd', padding: '20px', overflowY: 'auto', display: 'flex', flexDirection: 'column' },
  mainContent: { flex: 1, backgroundColor: '#fff', position: 'relative' },
  sidebarHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' },
  userSection: { display: 'flex', alignItems: 'center', gap: '10px' },
  logoutBtn: { background: '#ff4d4d', color: '#fff', border: 'none', padding: '4px 8px', borderRadius: '4px', cursor: 'pointer', fontSize: '11px' },
  inviteBox: { backgroundColor: '#fff9db', border: '1px solid #fcc419', padding: '12px', borderRadius: '8px', marginTop: '15px' },
  inviteRow: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '5px' },
  inviteActions: { display: 'flex', gap: '5px' },
  btnAccept: { backgroundColor: '#40c057', color: '#fff', border: 'none', padding: '3px 8px', borderRadius: '4px', cursor: 'pointer', fontSize: '11px' },
  btnReject: { backgroundColor: '#fa5252', color: '#fff', border: 'none', padding: '3px 8px', borderRadius: '4px', cursor: 'pointer', fontSize: '11px' },
  emptyState: { textAlign: 'center', marginTop: '30vh', color: '#adb5bd' },
  authWrapper: { display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#e9ecef' },
  authCard: { width: '400px', padding: '30px', backgroundColor: '#fff', borderRadius: '12px', boxShadow: '0 4px 15px rgba(0,0,0,0.1)' },
  toggleLink: { width: '100%', marginTop: '15px', background: 'none', border: 'none', color: '#007bff', cursor: 'pointer', fontSize: '14px' }
};

export default App;
