import React, { useState, useEffect } from 'react';
import Login from './Login';
import Register from './Register';
import UserSearch from './UserSearch';
import Chat from './Chat';

function App() {
  // Store the logged-in username
  const [authUser, setAuthUser] = useState(null);
  // Store the user we are currently chatting with {id, username}
  const [activeChat, setActiveChat] = useState(null);
  // Toggle between Login and Register screens
  const [showRegister, setShowRegister] = useState(false);

  // Check if user is already logged in when the app starts
  useEffect(() => {
    const savedUser = localStorage.getItem('username');
    const token = localStorage.getItem('access_token');
    if (savedUser && token) {
      setAuthUser(savedUser);
    }
  }, []);

  const handleLogout = () => {
    localStorage.clear(); // Removes tokens and username
    setAuthUser(null);
    setActiveChat(null);
  };

  return (
    <div style={{ fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif', padding: '20px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', borderBottom: '2px solid #eee', paddingBottom: '10px' }}>
        <h1 style={{ color: '#007bff' }}>SocialPlatform</h1>
        {authUser && (
          <div>
            <span style={{ marginRight: '15px' }}>Logged in as: <strong>{authUser}</strong></span>
            <button onClick={handleLogout} style={{ padding: '5px 10px', cursor: 'pointer', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '4px' }}>
              Logout
            </button>
          </div>
        )}
      </header>

      <main style={{ marginTop: '20px' }}>
        {!authUser ? (
          /* SHOW LOGIN OR REGISTER */
          <div style={{ maxWidth: '400px', margin: 'auto', textAlign: 'center' }}>
            {showRegister ? (
              <>
                <Register />
                <p>Already have an account? <button onClick={() => setShowRegister(false)} style={{ color: 'blue', border: 'none', background: 'none', cursor: 'pointer', textDecoration: 'underline' }}>Login here</button></p>
              </>
            ) : (
              <>
                <Login setAuthUser={(name) => {
                  setAuthUser(name);
                  localStorage.setItem('username', name); // Persist username
                }} />
                <p>Don't have an account? <button onClick={() => setShowRegister(true)} style={{ color: 'blue', border: 'none', background: 'none', cursor: 'pointer', textDecoration: 'underline' }}>Register here</button></p>
              </>
            )}
          </div>
        ) : (
          /* SHOW DASHBOARD: SEARCH (LEFT) & CHAT (RIGHT) */
          <div style={{ display: 'flex', gap: '30px' }}>
            <div style={{ flex: '1', borderRight: '1px solid #eee', paddingRight: '20px' }}>
              <UserSearch onSelectUser={(user) => setActiveChat(user)} />
            </div>

            <div style={{ flex: '2' }}>
              {activeChat ? (
                <Chat 
                  receiverId={activeChat.id} 
                  receiverName={activeChat.username} 
                />
              ) : (
                <div style={{ textAlign: 'center', marginTop: '100px', color: '#888' }}>
                  <h3>Welcome to your Dashboard</h3>
                  <p>Search for a friend on the left to start a real-time conversation.</p>
                </div>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
