import React, { useState } from 'react';
import api from './api';

// 1. Accept 'onSelectUser' as a prop from the parent (App.js)
const UserSearch = ({ onSelectUser }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleSearch = async (e) => {
        const value = e.target.value;
        setSearchTerm(value);

        if (value.length > 0) {
            setLoading(true);
            try {
                const response = await api.get(`users/?search=${value}`);
                setUsers(response.data);
            } catch (error) {
                console.error("Search failed:", error);
                if (error.response && error.response.status === 401) {
                    alert("Session expired. Please log in.");
                }
            }
            setLoading(false);
        } else {
            setUsers([]);
        }
    };

    return (
        <div style={{ padding: '20px', maxWidth: '400px', margin: 'auto' }}>
            <h3>Find Users</h3>
            <input
                type="text"
                placeholder="Type a username..."
                value={searchTerm}
                onChange={handleSearch}
                style={{
                    width: '100%',
                    padding: '10px',
                    borderRadius: '8px',
                    border: '1px solid #ddd'
                }}
            />

            {loading && <p>Searching...</p>}

            <div style={{ marginTop: '20px' }}>
                {users.map(user => (
                    <div key={user.id} style={{
                        padding: '10px',
                        borderBottom: '1px solid #eee',
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center'
                    }}>
                        <span><strong>{user.username}</strong></span>
                        
                        {/* 2. Updated Button: Calls onSelectUser with the user object */}
                        <button 
                            onClick={() => onSelectUser(user)}
                            style={{
                                backgroundColor: '#007bff',
                                color: 'white',
                                border: 'none',
                                padding: '5px 10px',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                        >
                            Chat
                        </button>
                    </div>
                ))}
                {!loading && searchTerm && users.length === 0 && (
                    <p>No users found with that name.</p>
                )}
            </div>
        </div>
    );
};

export default UserSearch;
