import React, { useState, useEffect } from 'react';
import api from './api'; // Your axios instance with the JWT token
import UserSearch from './UserSearch';
import Chat from './Chat';

function App() {
    const [friends, setFriends] = useState([]);
    const [selectedFriend, setSelectedFriend] = useState(null);
    const authUser = localStorage.getItem('username');

    // 1. Load your friend list from Django on startup
    useEffect(() => {
        const fetchFriends = async () => {
            try {
                // Ensure your Django backend has a 'friends/' endpoint
                const res = await api.get('friends/'); 
                setFriends(res.data);
            } catch (err) {
                console.error("Error loading friends list:", err);
            }
        };
        if (authUser) fetchFriends();
    }, [authUser]);

    // 2. This runs when you click a person in the Search results
    const handleUserSelected = (user) => {
        // Prevent adding duplicates to the sidebar
        const alreadyInList = friends.find(f => f.username === user.username);
        if (!alreadyInList) {
            setFriends([user, ...friends]);
        }
        // Open the chat window
        setSelectedFriend(user);
    };

    return (
        <div style={styles.container}>
            {/* --- SIDEBAR SECTION --- */}
            <div style={styles.sidebar}>
                <div style={styles.sidebarHeader}>
                    <h2 style={{ color: '#fff', margin: 0 }}>Messages</h2>
                </div>

                {/* Search Bar Integration */}
                <UserSearch onSelectUser={handleUserSelected} />

                <div style={styles.friendList}>
                    {friends.length === 0 ? (
                        <p style={styles.emptyText}>No chats yet. Search for a user to start!</p>
                    ) : (
                        friends.map(friend => (
                            <div 
                                key={friend.id || friend.username} 
                                style={{
                                    ...styles.friendRow,
                                    background: selectedFriend?.username === friend.username ? 'rgba(99, 102, 241, 0.2)' : 'transparent',
                                    borderLeft: selectedFriend?.username === friend.username ? '4px solid #6366f1' : '4px solid transparent'
                                }}
                                onClick={() => setSelectedFriend(friend)}
                            >
                                <div style={styles.avatar}>
                                    {friend.username[0].toUpperCase()}
                                </div>
                                <div style={styles.friendInfo}>
                                    <span style={styles.friendName}>{friend.username}</span>
                                    <span style={styles.onlineStatus}>Online</span>
                                </div>
                            </div>
                        ))
                    )}
                </div>
            </div>

            {/* --- CHAT SECTION --- */}
            <div style={styles.chatSection}>
                {selectedFriend ? (
                    <Chat 
                        receiverId={selectedFriend.id} 
                        receiverName={selectedFriend.username} 
                    />
                ) : (
                    <div style={styles.welcomeState}>
                        <div style={styles.welcomeCircle}>ðŸ’¬</div>
                        <h3 style={{ color: '#fff' }}>Welcome, {authUser}!</h3>
                        <p style={{ color: '#64748b' }}>Select a friend or search for someone to start chatting.</p>
                    </div>
                )}
            </div>
        </div>
    );
}

// --- CSS-IN-JS STYLES ---
const styles = {
    container: { display: 'flex', height: '100vh', width: '100vw', background: '#020617', overflow: 'hidden' },
    sidebar: { width: '320px', display: 'flex', flexDirection: 'column', borderRight: '1px solid rgba(255,255,255,0.05)', background: '#0f172a' },
    sidebarHeader: { padding: '24px', borderBottom: '1px solid rgba(255,255,255,0.05)' },
    friendList: { flex: 1, overflowY: 'auto', padding: '10px' },
    friendRow: { display: 'flex', alignItems: 'center', gap: '12px', padding: '12px', cursor: 'pointer', borderRadius: '12px', marginBottom: '4px', transition: 'all 0.2s' },
    avatar: { width: '40px', height: '40px', borderRadius: '10px', background: '#6366f1', color: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold' },
    friendInfo: { display: 'flex', flexDirection: 'column' },
    friendName: { color: '#fff', fontWeight: '600', fontSize: '14px' },
    onlineStatus: { color: '#10b981', fontSize: '11px', fontWeight: '500' },
    emptyText: { color: '#64748b', textAlign: 'center', padding: '20px', fontSize: '13px' },
    chatSection: { flex: 1, display: 'flex', background: '#020617' },
    welcomeState: { flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', textAlign: 'center' },
    welcomeCircle: { fontSize: '50px', marginBottom: '20px', background: 'rgba(99, 102, 241, 0.1)', width: '100px', height: '100px', display: 'flex', alignItems: 'center', justifyContent: 'center', borderRadius: '50%' }
};

export default App;
