import React, { useState, useEffect } from 'react';
import Login from './Login';
import Register from './Register';
import UserSearch from './UserSearch';
import Chat from './Chat';
import api from './api';

function App() {
  const [authUser, setAuthUser] = useState(localStorage.getItem('username'));
  const [activeChat, setActiveChat] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [invites, setInvites] = useState([]);

  // Fetch incoming invitations every 10 seconds or on load
  const fetchInvites = async () => {
    if (authUser) {
      try {
        const res = await api.get('invitations/'); // You'll need this endpoint in views.py
        setInvites(res.data);
      } catch (err) { console.error("Error fetching invites"); }
    }
  };

  useEffect(() => {
    fetchInvites();
    const interval = setInterval(fetchInvites, 10000);
    return () => clearInterval(interval);
  }, [authUser]);

  const handleInviteAction = async (inviteId, action) => {
    try {
      await api.post('invitations/respond/', { invite_id: inviteId, action });
      setInvites(prev => prev.filter(i => i.id !== inviteId));
      alert(`Invitation ${action}`);
    } catch (err) { alert("Action failed"); }
  };

  if (!authUser) {
    return (
      <div style={{ maxWidth: '400px', margin: '50px auto' }}>
        {showRegister ? <Register /> : <Login setAuthUser={setAuthUser} />}
        <button onClick={() => setShowRegister(!showRegister)} style={{ width: '100%', marginTop: '10px', background: 'none', border: 'none', color: 'blue', cursor: 'pointer' }}>
          {showRegister ? "Back to Login" : "Create an Account"}
        </button>
      </div>
    );
  }

  return (
    <div style={{ display: 'flex', height: '100vh', fontFamily: 'sans-serif' }}>
      {/* Sidebar */}
      <div style={{ width: '350px', borderRight: '1px solid #ddd', padding: '20px', overflowY: 'auto' }}>
        <h3>Social Platform</h3>
        <p>Logged in as: <b>{authUser}</b> <button onClick={() => { localStorage.clear(); window.location.reload(); }}>Logout</button></p>

        <hr />

        {/* Invitations Inbox */}
        {invites.length > 0 && (
          <div style={{ background: '#fff9c4', padding: '10px', borderRadius: '8px', marginBottom: '20px' }}>
            <h5 style={{ margin: '0 0 10px 0' }}>Chat Requests</h5>
            {invites.map(i => (
              <div key={i.id} style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '5px' }}>
                <span>{i.sender}</span>
                <div>
                  <button onClick={() => handleInviteAction(i.id, 'accepted')} style={{ background: 'green', color: 'white', border: 'none', fontSize: '10px' }}>Accept</button>
                  <button onClick={() => handleInviteAction(i.id, 'rejected')} style={{ background: 'red', color: 'white', border: 'none', fontSize: '10px', marginLeft: '5px' }}>Skip</button>
                </div>
              </div>
            ))}
          </div>
        )}

        <UserSearch onSelectUser={(user) => setActiveChat(user)} />
      </div>

      {/* Main Chat Area */}
      <div style={{ flex: 1, padding: '20px' }}>
        {activeChat ? (
          <Chat receiverId={activeChat.id} receiverName={activeChat.username} />
        ) : (
          <div style={{ textAlign: 'center', marginTop: '150px', color: '#888' }}>
            <h2>Welcome!</h2>
            <p>Select an accepted friend to start chatting.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
