import React, { useState } from 'react';
import api from './api'; // Use the custom api instance

const Login = ({ setAuthUser }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
    e.preventDefault();
    try {
        const res = await api.post('login/', { username, password });
        
        localStorage.setItem('access_token', res.data.access);
        localStorage.setItem('refresh_token', res.data.refresh);
        
        setAuthUser(username); 
        alert("Logged in successfully!");
    } catch (error) {
        // Safe error handling to prevent the "undefined" crash
        if (error.response) {
            // The server responded with an error (like 401 Invalid Credentials)
            console.log("Server Error Data:", error.response.data);
            alert("Login Failed: " + (error.response.data.detail || "Invalid Credentials"));
        } else if (error.request) {
            // The request was sent but no response was received (Server is likely down)
            console.log("No Response Object:", error.request);
            alert("Network Error: No response from server. Check if Django is running.");
        } else {
            // Something else went wrong
            alert("Error: " + error.message);
        }
    }
};

    return (
        <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '300px', margin: '20px auto', padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
            <h3 style={{ textAlign: 'center' }}>Login</h3>
            <input 
                type="text" 
                placeholder="Username" 
                value={username}
                onChange={(e) => setUsername(e.target.value)} 
                required 
                style={{ padding: '8px' }}
            />
            <input 
                type="password" 
                placeholder="Password" 
                value={password}
                onChange={(e) => setPassword(e.target.value)} 
                required 
                style={{ padding: '8px' }}
            />
            <button type="submit" style={{ padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
                Login
            </button>
        </form>
    );
};

export default Login;
