import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ setAuthUser }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        setError(''); // Clear previous errors

        try {
            // 1. Send login credentials to Django SimpleJWT endpoint
            const res = await axios.post('http://localhost:8000/api/login/', { 
                username, 
                password 
            });
            
            // 2. Save the 'access' token specifically for your api.js interceptor
            localStorage.setItem('access_token', res.data.access); 
            
            // 3. Save the 'refresh' token (useful for staying logged in longer later)
            localStorage.setItem('refresh_token', res.data.refresh);
            
            // 4. Save username for the UI state
            localStorage.setItem('username', username);
            
            // 5. Update parent state
            setAuthUser(username);
            
            // 6. Force a page reload to ensure the Axios Interceptor in api.js 
            // starts using the new token immediately
            window.location.reload(); 

        } catch (err) {
            console.error("Login error:", err);
            setError("Invalid username or password. Please try again.");
        }
    };

    return (
        <div style={styles.container}>
            <form onSubmit={handleLogin} style={styles.form}>
                <h3 style={{ textAlign: 'center', marginBottom: '20px' }}>Login</h3>
                
                {error && <p style={styles.error}>{error}</p>}

                <div style={styles.inputGroup}>
                    <label>Username</label>
                    <input 
                        type="text" 
                        value={username}
                        onChange={e => setUsername(e.target.value)} 
                        style={styles.input}
                        required 
                    />
                </div>

                <div style={styles.inputGroup}>
                    <label>Password</label>
                    <input 
                        type="password" 
                        value={password}
                        onChange={e => setPassword(e.target.value)} 
                        style={styles.input}
                        required 
                    />
                </div>

                <button type="submit" style={styles.button}>Sign In</button>
            </form>
        </div>
    );
};

// Simple inline styling
const styles = {
    container: { display: 'flex', justifyContent: 'center', padding: '20px' },
    form: { width: '100%', maxWidth: '300px', padding: '20px', border: '1px solid #ddd', borderRadius: '8px', background: '#fff' },
    inputGroup: { marginBottom: '15px' },
    input: { width: '100%', padding: '8px', marginTop: '5px', borderRadius: '4px', border: '1px solid #ccc', boxSizing: 'border-box' },
    button: { width: '100%', padding: '10px', backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' },
    error: { color: 'red', fontSize: '13px', textAlign: 'center' }
};

export default Login;
