import React, { useState, useEffect, useRef } from 'react';
import api from './api'; // Our interceptor

const Chat = ({ receiverId, receiverName }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const socketRef = useRef(null);

    // 1. Fetch Chat History when component loads
    useEffect(() => {
        const fetchHistory = async () => {
            try {
                const res = await api.get(`chat-history/${receiverId}/`);
                setMessages(res.data);
            } catch (err) {
                console.error("Could not fetch history", err);
            }
        };
        fetchHistory();

        // 2. Initialize WebSocket for real-time chat
        // Note: Using 'ws' protocol for WebSockets
        socketRef.current = new WebSocket(`ws://localhost:8000/ws/chat/${receiverName}/`);

        socketRef.current.onmessage = (e) => {
            const data = JSON.parse(e.data);
            setMessages((prev) => [...prev, { 
                content: data.message, 
                sender_username: data.sender_username 
            }]);
        };

        return () => socketRef.current.close();
    }, [receiverId, receiverName]);

    const sendMessage = (e) => {
        e.preventDefault();
        if (input.trim() === "") return;

        socketRef.current.send(JSON.stringify({
            'message': input,
            'receiver_id': receiverId
        }));
        setInput("");
    };

    return (
        <div style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '10px', marginTop: '20px' }}>
            <h4>Chatting with {receiverName}</h4>
            <div style={{ height: '250px', overflowY: 'auto', marginBottom: '10px', background: '#f9f9f9', padding: '10px' }}>
                {messages.map((msg, index) => (
                    <div key={index} style={{ marginBottom: '5px' }}>
                        <strong>{msg.sender_username}: </strong> {msg.content}
                    </div>
                ))}
            </div>
            <form onSubmit={sendMessage}>
                <input 
                    value={input} 
                    onChange={(e) => setInput(e.target.value)} 
                    placeholder="Type a message..." 
                    style={{ width: '80%', padding: '5px' }}
                />
                <button type="submit" style={{ width: '18%', marginLeft: '2%' }}>Send</button>
            </form>
        </div>
    );
};

export default Chat;
