// src/Login.js
import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ setAuthUser }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:8000/api/login/', { 
                username, 
                password 
            });
            
            console.log("Login Response Data:", res.data); // DEBUG LOG

            // Ensure we are saving the 'access' property from the response
            if (res.data.access) {
                localStorage.setItem('access_token', res.data.access); 
                localStorage.setItem('username', res.data.username);
                
                setAuthUser(username);
                window.location.reload(); 
            } else {
                console.error("Token not found in response. Check your Django SimpleJWT setup.");
            }
        } catch (err) {
            console.error("Login Error Details:", err.response?.data);
            alert("Login failed! Check console for details.");
        }
    };

    return (
        <form onSubmit={handleLogin} style={{ padding: '20px', border: '1px solid #ccc' }}>
            <h3>Login</h3>
            <input type="text" placeholder="Username" onChange={e => setUsername(e.target.value)} required />
            <br/><br/>
            <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} required />
            <br/><br/>
            <button type="submit">Login</button>
        </form>
    );
};

export default Login;
