import React, { useState, useEffect } from 'react';
import Login from './Login';
import Register from './Register';
import UserSearch from './UserSearch';
import Chat from './Chat';
import FriendsList from './FriendsList';
import api from './api';

function App() {
  const [authUser, setAuthUser] = useState(localStorage.getItem('username'));
  const [activeChat, setActiveChat] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [invites, setInvites] = useState([]);
  const [refreshFriends, setRefreshFriends] = useState(0);
  // Inside App.js
const [onlineUsers, setOnlineUsers] = useState({});

useEffect(() => {
    if (authUser) {
        const statusSocket = new WebSocket(`ws://localhost:8000/ws/status/`);

        statusSocket.onmessage = (event) => {
            const data = JSON.parse(event.data);
            setOnlineUsers(prev => ({
                ...prev,
                [data.user]: data.status // e.g., { "maddy": "online", "chaitu": "offline" }
            }));
        };

        return () => statusSocket.close();
    }
}, [authUser]);

  // 1. Fetch pending invitations for the Inbox
  const fetchInvites = async () => {
    if (authUser) {
      try {
        const res = await api.get('invitations/');
        setInvites(res.data);
      } catch (err) {
        console.error("Error fetching invites");
      }
    }
  };

  useEffect(() => {
    fetchInvites();
    const interval = setInterval(fetchInvites, 8000); // Polling every 8 seconds
    return () => clearInterval(interval);
  }, [authUser]);

  // 2. Handle Accept/Reject Invitation
  const handleInviteAction = async (inviteId, action) => {
    try {
      await api.post('invitations/respond/', { invite_id: inviteId, action });
      setInvites(prev => prev.filter(i => i.id !== inviteId));

      // If accepted, trigger the FriendsList to refresh
      if (action === 'accepted') {
        setRefreshFriends(prev => prev + 1);
      }
      alert(`Invitation ${action}`);
    } catch (err) {
      alert("Action failed");
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    setAuthUser(null);
    setActiveChat(null);
    window.location.reload();
  };

  if (!authUser) {
    return (
      <div style={styles.authContainer}>
        <h2 style={{ textAlign: 'center', color: '#007bff' }}>SocialPlatform</h2>
        {showRegister ? <Register /> : <Login setAuthUser={setAuthUser} />}
        <button onClick={() => setShowRegister(!showRegister)} style={styles.toggleBtn}>
          {showRegister ? "Already have an account? Login" : "New here? Create an account"}
        </button>
      </div>
    );
  }

  return (
    <div style={styles.appLayout}>
      {/* SIDEBAR: Social Logic */}
      <div style={styles.sidebar}>
        <div style={styles.sidebarHeader}>
          <h3>Social Chat</h3>
          <p style={{ fontSize: '12px' }}>User: <b>{authUser}</b></p>
          <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
        </div>

        {/* 1. SEARCH SECTION */}
        <div style={styles.section}>
          <UserSearch onSelectUser={(user) => setActiveChat(user)} />
        </div>

        {/* 2. INVITATION INBOX */}
        {invites.length > 0 && (
          <div style={styles.inviteInbox}>
            <h5 style={{ margin: '0 0 10px 0' }}>ðŸ”” Chat Requests</h5>
            {invites.map(i => (
              <div key={i.id} style={styles.inviteItem}>
                <span><b>{i.sender}</b></span>
                <div>
                  <button onClick={() => handleInviteAction(i.id, 'accepted')} style={styles.acceptBtn}>Accept</button>
                  <button onClick={() => handleInviteAction(i.id, 'rejected')} style={styles.rejectBtn}>Skip</button>
                </div>
              </div>
            ))}
          </div>
        )}

        <hr style={{ border: '0.5px solid #eee', margin: '20px 0' }} />

        {/* 3. FRIENDS LIST SECTION */}
        <div style={styles.section}>
          <FriendsList 
            onSelectUser={(user) => setActiveChat(user)} 
            refreshTrigger={refreshFriends} 
          />
        </div>
      </div>

      {/* MAIN CONTENT: Chat Logic */}
      <div style={styles.mainContent}>
        {activeChat ? (
          <Chat 
            receiverId={activeChat.id} 
            receiverName={activeChat.username} 
          />
        ) : (
          <div style={styles.welcomePlaceholder}>
            <img src="https://cdn-icons-png.flaticon.com/512/2462/2462719.png" alt="welcome" style={{ width: '100px', opacity: 0.5 }} />
            <h2>Your Dashboard</h2>
            <p>Select a friend from the list or search for new people to start chatting.</p>
          </div>
        )}
      </div>
    </div>
  );
}

// Inline Styles for a Professional Look
const styles = {
  appLayout: { display: 'flex', height: '100vh', backgroundColor: '#f5f7fb' },
  sidebar: { width: '350px', backgroundColor: '#fff', borderRight: '1px solid #e0e0e0', display: 'flex', flexDirection: 'column', padding: '20px', overflowY: 'auto' },
  sidebarHeader: { marginBottom: '20px' },
  mainContent: { flex: 1, display: 'flex', flexDirection: 'column', backgroundColor: '#fff' },
  authContainer: { maxWidth: '400px', margin: '100px auto', padding: '30px', backgroundColor: '#fff', borderRadius: '10px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' },
  toggleBtn: { width: '100%', marginTop: '15px', background: 'none', border: 'none', color: '#007bff', cursor: 'pointer', fontSize: '14px' },
  logoutBtn: { padding: '5px 10px', backgroundColor: '#f8d7da', color: '#721c24', border: '1px solid #f5c6cb', borderRadius: '4px', cursor: 'pointer', fontSize: '12px' },
  section: { marginBottom: '20px' },
  inviteInbox: { backgroundColor: '#fff3cd', padding: '15px', borderRadius: '8px', border: '1px solid #ffeeba' },
  inviteItem: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '5px' },
  acceptBtn: { backgroundColor: '#28a745', color: '#fff', border: 'none', borderRadius: '3px', padding: '3px 8px', fontSize: '11px', cursor: 'pointer' },
  rejectBtn: { backgroundColor: '#dc3545', color: '#fff', border: 'none', borderRadius: '3px', padding: '3px 8px', fontSize: '11px', cursor: 'pointer', marginLeft: '5px' },
  welcomePlaceholder: { textAlign: 'center', marginTop: '20vh', color: '#adb5bd' },
};

export default App;
