import React, { useEffect, useState } from 'react';
import api from './api';

const FriendsList = ({ onSelectUser, refreshTrigger, onlineUsers }) => {
    const [friends, setFriends] = useState([]);

    const fetchFriends = async () => {
        try {
            const res = await api.get('friends/');
            setFriends(res.data);
        } catch (err) {
            console.error("Error fetching friends", err);
        }
    };

    useEffect(() => {
        fetchFriends();
    }, [refreshTrigger]);

    return (
        <div style={styles.container}>
            {friends.map((friend) => {
                // Here we check the onlineUsers prop passed from App.js
                const isOnline = onlineUsers && onlineUsers[friend.username] === 'online';

                return (
                    <div 
                        key={friend.id} 
                        style={styles.card} 
                        onClick={() => onSelectUser({ id: friend.id, username: friend.username })}
                        onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#f8fafc'}
                        onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
                    >
                        <div style={styles.avatarWrapper}>
                            <div style={styles.avatar}>
                                {friend.username.charAt(0).toUpperCase()}
                            </div>
                            {/* The Status Dot */}
                            <div style={{
                                ...styles.statusDot,
                                backgroundColor: isOnline ? '#22c55e' : '#cbd5e1'
                            }}></div>
                        </div>

                        <div style={styles.info}>
                            <div style={styles.username}>{friend.username}</div>
                            <div style={styles.statusText}>
                                {isOnline ? 'Active now' : 'Offline'}
                            </div>
                        </div>
                    </div>
                );
            })}
            
            {friends.length === 0 && (
                <p style={styles.emptyText}>No friends connected yet.</p>
            )}
        </div>
    );
};

// Portfolio-ready Styles
const styles = {
    container: {
        marginTop: '10px',
    },
    card: {
        display: 'flex',
        alignItems: 'center',
        padding: '12px',
        borderRadius: '12px',
        cursor: 'pointer',
        transition: 'all 0.2s ease',
        marginBottom: '4px',
    },
    avatarWrapper: {
        position: 'relative',
        marginRight: '12px',
    },
    avatar: {
        width: '42px',
        height: '42px',
        borderRadius: '12px',
        backgroundColor: '#6366f1',
        color: '#fff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontWeight: 'bold',
        fontSize: '18px',
    },
    statusDot: {
        width: '12px',
        height: '12px',
        borderRadius: '50%',
        border: '2px solid #fff',
        position: 'absolute',
        bottom: '-2px',
        right: '-2px',
    },
    info: {
        flex: 1,
    },
    username: {
        fontWeight: '600',
        fontSize: '15px',
        color: '#1e293b',
    },
    statusText: {
        fontSize: '12px',
        color: '#64748b',
    },
    emptyText: {
        fontSize: '13px',
        color: '#94a3b8',
        textAlign: 'center',
        marginTop: '20px',
    }
};

export default FriendsList;
