import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Login from './Login';
import UserSearch from './UserSearch';
import Chat from './Chat';
import FriendsList from './FriendsList';
import api from './api';

function App() {
  const [authUser, setAuthUser] = useState(localStorage.getItem('username'));
  const [activeChat, setActiveChat] = useState(null);
  const [onlineUsers, setOnlineUsers] = useState({});

useEffect(() => {
    if (!authUser) return;

    // Connect to the Status WebSocket we just built in Django
    const statusSocket = new WebSocket(`ws://localhost:8000/ws/status/${authUser}/`);

    statusSocket.onmessage = (event) => {
        const data = JSON.parse(event.data);
        // This updates the "Green Dot" state across the entire app
        setOnlineUsers(prev => ({ 
            ...prev, 
            [data.user]: data.status 
        }));
    };

    return () => statusSocket.close();
}, [authUser]);

  if (!authUser) return <Login setAuthUser={setAuthUser} />;

  return (
    <div style={styles.dashboard}>
      {/* COLUMN 1: NAVIGATION RAIL (Minimalist) */}
      <motion.nav 
        initial={{ x: -80 }} animate={{ x: 0 }}
        style={styles.navRail}
      >
        <div style={styles.logoBox}>S</div>
        <div style={styles.navLinks}>
          <div style={styles.activeIcon}>ðŸ’¬</div>
          <div style={styles.icon}>ðŸ‘¥</div>
          <div style={styles.icon}>ðŸ””</div>
        </div>
        <button onClick={() => {localStorage.clear(); window.location.reload();}} style={styles.logoutBtn}>
          Power
        </button>
      </motion.nav>

      {/* COLUMN 2: SOCIAL SIDEBAR (Dynamic List) */}
      <motion.aside 
        initial={{ opacity: 0 }} animate={{ opacity: 1 }}
        style={styles.sidebar}
      >
        <header style={styles.sideHeader}>
          <h2 style={styles.title}>Messages</h2>
          <UserSearch onSelectUser={setActiveChat} />
        </header>

        <div style={styles.friendsContainer}>
          <FriendsList 
            onSelectUser={setActiveChat} 
            onlineUsers={onlineUsers} 
          />
        </div>
      </motion.aside>

      {/* COLUMN 3: CHAT VIEWPORT (Spring Animation) */}
      <main style={styles.mainView}>
        <AnimatePresence mode="wait">
          {activeChat ? (
            <motion.div 
              key={activeChat.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.95 }}
              transition={{ type: "spring", stiffness: 300, damping: 30 }}
              style={{ height: '100%' }}
            >
              <Chat receiverId={activeChat.id} receiverName={activeChat.username} />
            </motion.div>
          ) : (
            <div style={styles.emptyState}>
              <motion.div 
                animate={{ y: [0, -10, 0] }} 
                transition={{ repeat: Infinity, duration: 4 }}
                style={styles.emptyIcon}
              >âœ¨</motion.div>
              <h3>Pick up where you left off</h3>
              <p>Select a friend to open a secure encrypted channel.</p>
            </div>
          )}
        </AnimatePresence>
      </main>
    </div>
  );
}

const styles = {
  dashboard: { display: 'flex', height: '100vh', width: '100vw' },
  navRail: { 
    width: '80px', background: 'var(--bg-surface)', borderRight: '1px solid var(--bg-accent)',
    display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '25px 0' 
  },
  logoBox: { 
    width: '45px', height: '45px', background: 'var(--primary)', borderRadius: '14px',
    display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold',
    boxShadow: '0 0 20px var(--primary-glow)', marginBottom: '50px'
  },
  navLinks: { flex: 1, display: 'flex', flexDirection: 'column', gap: '30px' },
  activeIcon: { fontSize: '24px', background: 'var(--bg-accent)', padding: '12px', borderRadius: '15px' },
  icon: { fontSize: '24px', opacity: 0.5, cursor: 'pointer' },
  sidebar: { 
    width: '360px', background: 'var(--bg-deep)', borderRight: '1px solid var(--bg-accent)',
    display: 'flex', flexDirection: 'column' 
  },
  sideHeader: { padding: '30px 25px' },
  title: { fontSize: '28px', fontWeight: '700', marginBottom: '20px' },
  friendsContainer: { flex: 1, overflowY: 'auto', padding: '0 15px' },
  mainView: { flex: 1, position: 'relative', background: 'var(--bg-surface)' },
  emptyState: { 
    height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', 
    justifyContent: 'center', textAlign: 'center', color: 'var(--text-muted)' 
  },
  emptyIcon: { fontSize: '60px', marginBottom: '20px' },
  logoutBtn: { border: 'none', background: 'none', color: '#ef4444', fontWeight: 'bold', cursor: 'pointer' }
};

export default App;
