import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import api from './api'; 

const Chat = ({ receiverId, receiverName }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const socket = useRef(null);
    const scrollRef = useRef(null);
    const authUser = localStorage.getItem('username');

    // 1. Auto-scroll to the bottom when a new message arrives
    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
        }
    }, [messages]);

    useEffect(() => {
        if (!authUser || !receiverName) return;

        // 2. FETCH HISTORICAL MESSAGES FROM DATABASE
        const fetchHistory = async () => {
            try {
                // This call ensures messages are loaded even if you refresh
                const res = await api.get(`messages/${receiverName}/`);
                const history = res.data.map(m => ({
                    sender: m.sender_username,
                    message: m.content,
                    timestamp: new Date(m.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
                }));
                setMessages(history);
            } catch (err) {
                console.error("Chat history fetch failed:", err);
            }
        };

        fetchHistory();

        // 3. ESTABLISH WEBSOCKET CONNECTION
        // The sort() is critical: both 'Aryan_Maddy' and 'Maddy_Aryan' become 'Aryan_Maddy'
        const roomName = [authUser, receiverName].sort().join("_");
        
        // Use your Local IP for phone testing, or localhost for PC
        socket.current = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/`);

        socket.current.onmessage = (e) => {
            const data = JSON.parse(e.data);
            
            // This functional update ensures we don't lose previous messages
            setMessages((prev) => [...prev, { 
                sender: data.sender, 
                message: data.message,
                timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
            }]);
        };

        socket.current.onopen = () => console.log("âœ… Chat Connected to:", roomName);
        socket.current.onclose = () => console.warn("âŒ Chat Disconnected");

        return () => {
            if (socket.current) socket.current.close();
        };
    }, [receiverName, authUser]);

    const sendMessage = () => {
        if (input.trim() && socket.current && socket.current.readyState === WebSocket.OPEN) {
            socket.current.send(JSON.stringify({ 
                'message': input, 
                'sender': authUser 
            }));
            setInput("");
        }
    };

    return (
        <div style={styles.chatWrapper}>
            {/* Chat Header */}
            <header style={styles.header}>
                <div style={styles.headerInfo}>
                    <div style={styles.avatar}>{receiverName[0].toUpperCase()}</div>
                    <div>
                        <h4 style={{ margin: 0, color: '#fff' }}>{receiverName}</h4>
                        <span style={styles.statusLine}>Secure Connection Active</span>
                    </div>
                </div>
            </header>

            {/* Message Area */}
            <div style={styles.messageArea} ref={scrollRef}>
                <AnimatePresence initial={false}>
                    {messages.map((msg, i) => {
                        const isMe = msg.sender === authUser;
                        return (
                            <motion.div 
                                key={i}
                                initial={{ opacity: 0, y: 15, scale: 0.95 }}
                                animate={{ opacity: 1, y: 0, scale: 1 }}
                                style={{
                                    ...styles.bubble,
                                    alignSelf: isMe ? 'flex-end' : 'flex-start',
                                    background: isMe ? 'linear-gradient(135deg, #6366f1, #4f46e5)' : 'rgba(255,255,255,0.08)',
                                    borderBottomRightRadius: isMe ? '4px' : '18px',
                                    borderBottomLeftRadius: isMe ? '18px' : '4px',
                                }}
                            >
                                <div style={styles.messageText}>{msg.message}</div>
                                <div style={styles.timeTag}>{msg.timestamp}</div>
                            </motion.div>
                        );
                    })}
                </AnimatePresence>
            </div>

            {/* Input Bar */}
            <div style={styles.inputContainer}>
                <div style={styles.inputBar}>
                    <input 
                        type="text" 
                        placeholder="Type a message..." 
                        style={styles.chatInput}
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                    />
                    <motion.button 
                        whileHover={{ scale: 1.1, x: 3 }}
                        whileTap={{ scale: 0.9 }}
                        onClick={sendMessage} 
                        style={styles.sendBtn}
                    >
                        ðŸš€
                    </motion.button>
                </div>
            </div>
        </div>
    );
};

const styles = {
    chatWrapper: { display: 'flex', flexDirection: 'column', height: '100%', background: '#020617' },
    header: { padding: '16px 24px', background: 'rgba(15, 23, 42, 0.7)', backdropFilter: 'blur(12px)', borderBottom: '1px solid rgba(255,255,255,0.05)', zIndex: 10 },
    headerInfo: { display: 'flex', alignItems: 'center', gap: '14px' },
    avatar: { width: '42px', height: '42px', borderRadius: '12px', background: '#6366f1', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', color: '#fff' },
    statusLine: { fontSize: '10px', color: '#10b981', fontWeight: 'bold', textTransform: 'uppercase' },
    messageArea: { flex: 1, padding: '24px', overflowY: 'auto', display: 'flex', flexDirection: 'column', gap: '12px' },
    bubble: { padding: '12px 16px', maxWidth: '70%', color: '#fff', boxShadow: '0 4px 15px rgba(0,0,0,0.2)' },
    messageText: { fontSize: '15px', lineHeight: '1.4' },
    timeTag: { fontSize: '9px', opacity: 0.5, textAlign: 'right', marginTop: '4px' },
    inputContainer: { padding: '20px', background: '#020617' },
    inputBar: { display: 'flex', background: 'rgba(255,255,255,0.03)', borderRadius: '18px', padding: '5px 15px', border: '1px solid rgba(255,255,255,0.1)' },
    chatInput: { flex: 1, background: 'none', border: 'none', color: '#fff', outline: 'none', padding: '10px', fontSize: '15px' },
    sendBtn: { background: 'none', border: 'none', fontSize: '22px', cursor: 'pointer' }
};

export default Chat;
