import React, { useState } from 'react';
import UserSearch from './UserSearch';
import Chat from './Chat';

function App() {
  const [activeChat, setActiveChat] = useState(null); // Stores {id, username}

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h1>My Social Platform</h1>

      <div style={{ display: 'flex', gap: '40px' }}>
        {/* Left Side: Search */}
        <div style={{ flex: 1 }}>
          <UserSearch onSelectUser={(user) => setActiveChat(user)} />
        </div>

        {/* Right Side: Chat Window */}
        <div style={{ flex: 1 }}>
          {activeChat ? (
            <Chat 
                receiverId={activeChat.id} 
                receiverName={activeChat.username} 
            />
          ) : (
            <p style={{ marginTop: '100px', color: '#888' }}>Select a user to start chatting</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
