import React, { useEffect, useState } from 'react';
import api from './api';

const FriendsList = ({ onSelectUser, refreshTrigger, onlineUsers }) => {
    const [friends, setFriends] = useState([]);

    const fetchFriends = async () => {
        try {
            const res = await api.get('friends/');
            setFriends(res.data);
        } catch (err) { console.error("Error fetching friends"); }
    };

    useEffect(() => {
        fetchFriends();
    }, [refreshTrigger]);

    const handleUnfriend = async (inviteId) => {
        if (window.confirm("Remove this friend?")) {
            await api.delete(`friends/remove/${inviteId}/`);
            setFriends(prev => prev.filter(f => f.invite_id !== inviteId));
            onSelectUser(null);
        }
    };

    return (
        <div style={{ marginTop: '20px' }}>
            <h4 style={{ color: '#555', marginBottom: '10px' }}>Your Friends</h4>
            {friends.map(friend => {
                const isOnline = onlineUsers[friend.username] === 'online';
                
                return (
                    <div key={friend.id} style={styles.friendCard}>
                        <div 
                            style={styles.clickable} 
                            onClick={() => onSelectUser({ id: friend.id, username: friend.username })}
                        >
                            <div style={styles.avatarWrapper}>
                                <span style={{ fontSize: '20px' }}>ðŸ‘¤</span>
                                {/* Online/Offline Dot */}
                                <div style={{
                                    ...styles.statusDot,
                                    backgroundColor: isOnline ? '#2ecc71' : '#bdc3c7'
                                }}></div>
                            </div>
                            <span style={{ 
                                fontWeight: 'bold', 
                                color: isOnline ? '#2c3e50' : '#7f8c8d' 
                            }}>
                                {friend.username}
                            </span>
                        </div>
                        <button onClick={() => handleUnfriend(friend.invite_id)} style={styles.removeLink}>Remove</button>
                    </div>
                );
            })}
            {friends.length === 0 && <p style={{ fontSize: '12px', color: '#999' }}>No friends yet.</p>}
        </div>
    );
};

const styles = {
    friendCard: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px', backgroundColor: '#f8f9fa', borderRadius: '8px', marginBottom: '8px', border: '1px solid #eee' },
    clickable: { display: 'flex', alignItems: 'center', cursor: 'pointer', flex: 1 },
    avatarWrapper: { position: 'relative', marginRight: '12px' },
    statusDot: { position: 'absolute', bottom: '0', right: '0', width: '10px', height: '10px', borderRadius: '50%', border: '2px solid white' },
    removeLink: { background: 'none', border: 'none', color: '#e74c3c', fontSize: '11px', cursor: 'pointer' }
};

export default FriendsList;
